INSERT INTO `tbl_news` (`News_ID`, `Cate_ID`, `News_Name`, `Description`, `Image`, `Time`, `Descrip_Demo`) VALUES
('KT_01', 17, 'RoyalHome mang đến sức hút từ thiết kế nội thất phong cách hiện đại', '<p><em>Phong c&aacute;ch thiết kế nội thất hiện đại đang dần trở th&agrave;nh xu hướng nổi bật tr&ecirc;n thị trường. Bằng việc ưu ti&ecirc;n sử dụng c&aacute;c tone m&agrave;u trang nh&atilde;, nhẹ nh&agrave;ng c&ugrave;ng c&aacute;c chi tiết trang tr&iacute; nội thất đơn giản, những thiết kế nội thất hiện đại lu&ocirc;n mang đến cảm gi&aacute;c nhẹ nh&agrave;ng, thoải m&aacute;i cho gia chủ.</em></p>\r\n\r\n<p><img style=\"width:100%;\" alt=\"\" src=\"https://thietkeroyalhome.com/upload_images/images/chungcuparkhill-anhtrieu-phongkhach6.jpg\" /></p>\r\n\r\n<p>Ph&ograve;ng kh&aacute;ch được xem l&agrave; bộ mặt của ng&ocirc;i nh&agrave;. Đ&oacute; ch&iacute;nh l&agrave; l&yacute; do tại sao c&aacute;c kiến tr&uacute;c sư của RoyalHome lu&ocirc;n đầu tư phần lớn thời gian v&agrave;o việc thống nhất thiết kế nội thất kh&ocirc;ng gian ph&ograve;ng kh&aacute;ch với gia chủ.</p>\r\n\r\n<p><img style=\"width:100%;\" alt=\"\" src=\"https://thietkeroyalhome.com/upload_images/images/%C3%A1%C2%BA%C2%A3nh/n1.jpg\" /></p>\r\n\r\n<p>Với phong c&aacute;ch hiện đại, RoyalHome lu&ocirc;n ưu ti&ecirc;n ph&ograve;ng bếp, ăn được thiết kế mở, trực tiếp liền kề ph&ograve;ng kh&aacute;ch tạo cảm gi&aacute;c gần gũi v&agrave; tăng t&iacute;nh kết nối trong sinh hoạt gia đ&igrave;nh.&nbsp;B&agrave;n ăn được đặt cạnh cửa sổ lớn, l&agrave;m bằng k&iacute;nh trong suốt mang đến một kh&ocirc;ng gian trở n&ecirc;n gần gũi với thi&ecirc;n nhi&ecirc;n. Nội thất bếp được thiết kế mở, trực tiếp liền kề ph&ograve;ng kh&aacute;ch tạo cảm gi&aacute;c gần gũi v&agrave; tăng t&iacute;nh nối kết trong sinh hoạt gia đ&igrave;nh. Hệ thống tủ kệ m&agrave;u trắng kem của kh&ocirc;ng gian bếp một lần nữa thể hiện sự trang nh&atilde;, nhẹ nh&agrave;ng v&agrave; thống nhất với tổng thể nội thất căn nh&agrave; phố.</p>\r\n\r\n<p><img style=\"width:100%;\" alt=\"\" src=\"https://thietkeroyalhome.com/upload_images/images/chungcuparkhill-anhtrieu-phongngu7-1.jpg\" /></p>\r\n\r\n<p>Thiết kế nội thất ph&ograve;ng ngủ Master được trang bị đầy đủ tiện nghi mang đến những ph&uacute;t gi&acirc;y thư gi&atilde;n tuyệt đối cho gia chủ.&nbsp;Nếu ở nội thất ph&ograve;ng kh&aacute;ch, m&agrave;u n&acirc;u nhạt thể hiện sự nhẹ nh&agrave;ng, tinh tế th&igrave; ở nội thất ph&ograve;ng ngủ Master sắc m&agrave;u n&agrave;y mang đến cảm gi&aacute;c ấm &aacute;p nhờ sự kết hợp với s&agrave;n gỗ n&acirc;u đậm. B&agrave;n l&agrave;m việc bố tr&iacute; gần giường ngủ thuận tiện cho gia chủ l&agrave;m việc, nghỉ ngơi. Ngo&agrave;i ra, thiết kế nội thất ph&ograve;ng ngủ Master được bố tr&iacute; ph&ograve;ng thay đồ t&aacute;ch biệt bởi cửa k&eacute;o, liền kề sau đ&oacute; l&agrave; ph&ograve;ng tắm với bồn tắm nằm hiện đại. Thiết kế nội thất ph&ograve;ng ngủ Master được trang bị đầy đủ tiện nghi mang đến những ph&uacute;t gi&acirc;y thư gi&atilde;n tuyệt đối cho gia chủ.</p>\r\n\r\n<p>Từ lối sử dụng m&agrave;u sắc, chất liệu v&agrave; c&aacute;c bố cục, thiết kế nội thất đ&atilde; chinh phục được guu thẩm mỹ tinh tế của gia chủ. Căn nh&agrave; phố sau giai đoạn ho&agrave;n thiện nội thất được kho&aacute;t l&ecirc;n m&igrave;nh n&eacute;t nhẹ nh&agrave;ng, dễ chịu b&ecirc;n cạnh vẻ hiện đại, sang trọng. Bất cứ ai sở hữu kh&ocirc;ng gian tuyệt vời n&agrave;y cũng sẽ rất tự h&agrave;o.Qu&yacute; kh&aacute;ch quan t&acirc;m đến thiết kế thi c&ocirc;ng nội thất vui l&ograve;ng li&ecirc;n hệ RoyalHome, nh&acirc;n vi&ecirc;n tư vấn của ch&uacute;ng t&ocirc;i lu&ocirc;n sẵn s&agrave;ng hỗ trợ.</p>\r\n', 'struc_img/kt1.jpg', '2019-01-23', ''),
('KT_02', 17, 'Nhật ký thi công nội thất công trình BT07-H7- TT1 (Khu biệt thự Star Lake)', '<p><em>Mục đ&iacute;ch cao nhất m&agrave; C&ocirc;ng ty Cổ phần&nbsp;X&acirc;y dựng v&agrave;&nbsp;Nội thất RoyalHome lu&ocirc;n muốn hướng đến ch&iacute;nh l&agrave; sự h&agrave;i l&ograve;ng của kh&aacute;ch h&agrave;ng về cả thiết kế lẫn chất lượng thi c&ocirc;ng. Hiện tại, RoyalHome đang tiến h&agrave;ng thi c&ocirc;ng c&ocirc;ng tr&igrave;nh BT07-H7- TT1 (Khu biệt thự Star Lake). Qu&aacute; tr&igrave;nh thi c&ocirc;ng thiết kế nội thất n&agrave;y đ&ograve;i hỏi sự kết hợp chặt chẽ giữa c&aacute;c phộ phận, sự t&acirc;m huyết v&agrave; tr&aacute;ch nhiệm của những người tham gia v&agrave;o thi c&ocirc;ng. Đặc biệt hơn cả ch&iacute;nh l&agrave; năng lực chuy&ecirc;n m&ocirc;n v&agrave; tinh thần tr&aacute;ch nhiệm cao với nghề của người gi&aacute;m s&aacute;t thi c&ocirc;ng nội thất RoyalHome.</em></p>\r\n\r\n<p><img style=\"width:50%;\" alt=\"\" src=\"https://thietkeroyalhome.com/upload_images/images/61365741_1144515809088288_1665112572451356672_n.jpg\" /></p>\r\n\r\n<p><em>Thi c&ocirc;ng c&ocirc;ng tr&igrave;nh BT07-H7- TT1 (Khu biệt thự Star Lake)</em></p>\r\n\r\n<p><strong>Chất lượng thi c&ocirc;ng nội thất c&ocirc;ng tr&igrave;nh BT07-H7- TT1 (Khu biệt thự Star Lake)</strong></p>\r\n\r\n<p>Khi bắt tay v&agrave;o thi c&ocirc;ng c&ocirc;ng tr&igrave;nh nội thất, tiến độ thi c&ocirc;ng cần phải được l&agrave;m r&otilde; v&agrave; tu&acirc;n theo đ&uacute;ng tr&igrave;nh tự nhất c&oacute; thể để đảm bảo thời gian nhận nh&agrave; cho gia chủ. Tiến độ c&oacute; thể xem như một lời hứa b&agrave;n giao c&ocirc;ng tr&igrave;nh ho&agrave;n thiện cho gia chủ. Lời hứa đ&oacute; cũng ch&iacute;nh l&agrave; uy t&iacute;n, l&agrave; niềm tin m&agrave; RoyalHome lu&ocirc;n muốn tạo dựng trong l&ograve;ng kh&aacute;ch h&agrave;ng. Kh&aacute;ch h&agrave;ng kh&ocirc;ng mong muốn khi lịch đ&atilde; đến ng&agrave;y b&agrave;n giao nh&agrave; nhưng những g&igrave; họ nhận được chỉ l&agrave; một mớ lộn xộn chưa xong xu&ocirc;i g&igrave; cả đang &ldquo;ngự trị&rdquo; trong ch&iacute;nh nh&agrave; m&igrave;nh v&agrave; lại tiếp tục những ng&agrave;y th&aacute;ng chờ đợi mệt mỏi.&nbsp;Hiểu được điều n&agrave;y, RoyalHome lu&ocirc;n cố gắng tu&acirc;n theo tiến độ đề ra một c&aacute;ch s&aacute;t sao nhất.&nbsp;</p>\r\n\r\n<p><img style=\"width:100%;\" alt=\"\" src=\"https://thietkeroyalhome.com/upload_images/images/60362519_2208878792535840_1171775470438449152_o.jpg\" /></p>\r\n\r\n<p><em>C&ocirc;ng t&aacute;c chuẩn bị thi c&ocirc;ng c&ocirc;ng tr&igrave;nh BT07-H7- TT1 (Khu biệt thự Star Lake)</em></p>\r\n\r\n<p><img style=\"width:100%;\" alt=\"\" src=\"https://thietkeroyalhome.com/upload_images/images/60683423_2218772881546431_639167429443846144_o.jpg\" /></p>\r\n\r\n<p><em>C&ocirc;ng t&aacute;c chuẩn bị thi c&ocirc;ng c&ocirc;ng tr&igrave;nh BT07-H7- TT1 (Khu biệt thự Star Lake)</em></p>\r\n\r\n<p>RoyalHome Việt Nam l&agrave; đơn vị đ&atilde; c&oacute; nhiều kinh nghiệm trong thiết kế nội thất biệt thự, căn hộ chung cư, nh&agrave; phố, văn ph&ograve;ng, c&ocirc;ng tr&igrave;nh thương mại,.... Sản phẩm thiết kế nội thất lu&ocirc;n được ho&agrave;n thiện theo những g&igrave; đ&atilde; cam kết với kh&aacute;ch h&agrave;ng.</p>\r\n\r\n<p>Đ&acirc;y l&agrave; những h&igrave;nh ảnh chi tiết qu&aacute; tr&igrave;nh thi c&ocirc;ng c&ocirc;ng tr&igrave;nh BT07-H7- TT1 (Khu biệt thự Star Lake). C&ocirc;ng tr&igrave;nh hiện đang đang trong giai đoạn thi c&ocirc;ng phần th&ocirc;.</p>\r\n\r\n<p><img style=\"width:100%;\" alt=\"\" src=\"https://thietkeroyalhome.com/upload_images/images/60281908_2208879052535814_7013837239687839744_n.jpg\" /></p>\r\n\r\n<p><em>Thi c&ocirc;ng hạng mục ph&aacute; dỡ c&ocirc;ng tr&igrave;nh BT07-H7- TT1 (Khu biệt thự Star Lake)</em></p>\r\n\r\n<p>Ở giai đoạn n&agrave;y sẽ định h&igrave;nh kh&ocirc;ng gian như trần tường s&agrave;n bao gồm việc ph&aacute; dỡ, x&acirc;y mới c&aacute;c hệ thống&nbsp;tường, v&aacute;ch v&agrave; ho&agrave;n thiện nền, s&agrave;n. Thi c&ocirc;ng c&aacute;c hạng mục nước, điện, điện nhẹ, th&ocirc;ng tin li&ecirc;n lạc, hệ thống th&ocirc;ng kh&iacute;, điều h&ograve;a kh&ocirc;ng kh&iacute;, tiếp đ&oacute; l&agrave; trần,... ở giai đoạn n&agrave;y chủ yếu thi c&ocirc;ng tại c&ocirc;ng trường. Điểm mẫu chốt lớn nhất l&agrave; việc t&iacute;nh to&aacute;n hợp l&yacute; giữa c&aacute;c tổ đội, nh&agrave; thầu để sao cho hiệu quả v&agrave; kh&ocirc;ng chồng ch&eacute;o, cản trở nhau.</p>\r\n\r\n<p><img style=\"width:100%;\" alt=\"\" src=\"https://thietkeroyalhome.com/upload_images/images/60344432_2210570342366685_6001354323777290240_n.jpg\" /></p>\r\n\r\n<p><em>Thi c&ocirc;ng hạng mục ph&aacute; dỡ c&ocirc;ng tr&igrave;nh BT07-H7- TT1 (Khu biệt thự Star Lake)</em></p>\r\n\r\n<p>Để c&ocirc;ng tr&igrave;nh đạt chất lượng tốt nhất, gi&aacute;m s&aacute;t thi c&ocirc;ng của RoyalHome lu&ocirc;n phải d&otilde;i theo qu&aacute; tr&igrave;nh thi c&ocirc;ng một c&aacute;ch thường xuy&ecirc;n. Nhưng kh&aacute;c với những người thiết kế kiến tr&uacute;c, qu&aacute; tr&igrave;nh gi&aacute;m s&aacute;t thi c&ocirc;ng diễn ra từ khi c&ocirc;ng tr&igrave;nh mới bắt đầu, thiết bị, nguy&ecirc;n liệu, hiện trạng c&ocirc;ng tr&igrave;nh vẫn c&ograve;n bề bộn, ngổn ngang. &nbsp;&nbsp;&nbsp;</p>\r\n\r\n<p><img style=\"width:100%;\" alt=\"\" src=\"https://thietkeroyalhome.com/upload_images/images/60361553_2212186375538415_7897103758100791296_n.jpg\" /></p>\r\n\r\n<p><em>Thi c&ocirc;ng hạng mục ph&aacute; dỡ c&ocirc;ng tr&igrave;nh BT07-H7- TT1 (Khu biệt thự Star Lake)</em></p>\r\n\r\n<p>Tuy nhi&ecirc;n, việc gi&aacute;m s&aacute;t thi c&ocirc;ng nội thất cũng kh&ocirc;ng v&igrave; thế m&agrave; dễ d&agrave;ng. C&ocirc;ng việc gi&aacute;m s&aacute;t n&agrave;o cũng đ&ograve;i hỏi sự tỉ mỉ, quan s&aacute;t v&agrave; kiểm tra đến từng chi tiết, t&acirc;m huyết với c&ocirc;ng việc v&agrave; lấy chất lượng c&ocirc;ng tr&igrave;nh l&agrave;m ti&ecirc;u ch&iacute; h&agrave;ng đầu.</p>\r\n\r\n<p><img style=\"width:100%;\" alt=\"\" src=\"https://thietkeroyalhome.com/upload_images/images/60489004_2213935202030199_3134123775755288576_n.jpg\" /></p>\r\n\r\n<p><em>Thi c&ocirc;ng bảo vệ cửa c&ocirc;ng tr&igrave;nh BT07-H7- TT1 (Khu biệt thự Star Lake)</em></p>\r\n\r\n<p>Mục đ&iacute;ch của hoạt động gi&aacute;m s&aacute;t thi c&ocirc;ng nhằm x&aacute;c nhận cho c&ocirc;ng tr&igrave;nh đảm bảo đ&uacute;ng thiết kế, theo đ&uacute;ng quy chuẩn, ti&ecirc;u chuẩn thiết kế, đạt chất lượng để đưa v&agrave;o nghiệm thu. Như vậy, mọi c&ocirc;ng tr&igrave;nh trong qu&aacute; tr&igrave;nh thi c&ocirc;ng nội thất đều được RoyalHome thực hiện chế độ gi&aacute;m s&aacute;t nghi&ecirc;m ngặt.</p>\r\n\r\n<p><img style=\"width:100%;\" alt=\"\" src=\"https://thietkeroyalhome.com/upload_images/images/60765274_2217101001713619_2380534663744585728_n.jpg\" /></p>\r\n\r\n<p><em>Thi c&ocirc;ng hạng mục ph&aacute; dỡ c&ocirc;ng tr&igrave;nh BT07-H7- TT1 (Khu biệt thự Star Lake)</em></p>\r\n\r\n<p><strong>Kh&aacute;ch h&agrave;ng của RoyalHome n&oacute;i g&igrave; về thi c&ocirc;ng nội thất?</strong></p>\r\n\r\n<p>Trong ng&agrave;nh nội thất, thiết kế l&agrave; phần dạo đầu với những &yacute; tưởng s&aacute;ng tạo cho kh&ocirc;ng gian sống. N&oacute; mang &yacute; nghĩa phong c&aacute;ch theo sở th&iacute;ch của nh&agrave; đầu tư. Nhưng để được sản phẩm chất lượng lại l&agrave; chuy&ecirc;n m&ocirc;n của b&ecirc;n thi c&ocirc;ng. Nếu bạn muốn c&oacute; được sản phẩm chất lượng, th&igrave; kinh ph&iacute; kh&ocirc;ng phải thấp. Nh&agrave; đầu tư muốn đặt đồ theo gi&aacute; cạnh tranh, th&igrave; sẽ kh&ocirc;ng bao giờ c&oacute; được sản phẩm chất lượng.</p>\r\n\r\n<p>Tiền lương cho 1 người thợ b&igrave;nh thường trung b&igrave;nh 8tr/th&aacute;ng, 1 người thợ chuy&ecirc;n nghiệp trung b&igrave;nh 15tr/th&aacute;ng. Tiền phụ liệu cho đồ gỗ chất lượng cao gấp 2 lần so với sản phẩm c&oacute; gi&aacute; cạnh tranh (gỗ c&ocirc;ng nghiệp). Tiền c&ocirc;ng cho đồ gỗ chất lượng cao gấp 2 lần so với đồ gỗ c&oacute; gi&aacute; cạnh tranh. Vậy thử hỏi rằng sản phẩm mang gi&aacute; cạnh tranh sẽ như thế n&agrave;o?</p>\r\n\r\n<p>Ở Việt Nam, đa số người th&iacute;ch của rẻ m&agrave; đẹp, c&ograve;n bền hay kh&ocirc;ng th&igrave; kh&ocirc;ng quan trọng, miễn sao c&oacute; đồ x&agrave;i l&agrave; được. Cho n&ecirc;n nhiều người hay c&oacute; sự ngộ nhận, rồi so b&igrave; gi&aacute; nơi n&agrave;y cao, gi&aacute; nơi kia thấp, người ta gọi l&agrave; đi d&ograve; gi&aacute;, điều n&agrave;y tai hại lắm. C&aacute;i n&agrave;o c&oacute; gi&aacute; của c&aacute;i đ&oacute;.</p>\r\n\r\n<p>Nếu l&agrave; những thiết kế đơn giản, bạn c&oacute; thể li&ecirc;n hệ trực tiếp với c&aacute;c xưởng mộc, thợ họ l&agrave;m quen họ sẽ l&ecirc;n thiết kế cho, qua đ&oacute; cũng nắm được kinh nghiệm của họ, họ tư vấn cho m&igrave;nh lu&ocirc;n l&agrave;m chất liệu gỗ g&igrave; th&igrave; ph&ugrave; hợp, rồi c&ograve;n hợp với t&uacute;i tiền của m&igrave;nh nữa.</p>\r\n\r\n<p>L&agrave;m trực tiếp ở xưởng gi&aacute; sẽ được ưu đ&atilde;i hơn. Nếu bằng gi&aacute; so với thiết kế th&igrave; chắc chắn sản phẩm của họ phải hơn. V&igrave; khi qua trung gian họ sẽ c&oacute; c&aacute;ch để l&agrave;m giảm gi&aacute; m&agrave; ch&uacute;ng ta kh&ocirc;ng biết được. N&ecirc;n nếu lựa chọn trực tiếp được xưởng mộc l&agrave;m l&agrave; tốt nhất.</p>\r\n\r\n<p>Truy cập thường xuy&ecirc;n Website v&agrave; Fanpage để theo d&otilde;i tiến tr&igrave;nh thi c&ocirc;ng nội thất c&ocirc;ng tr&igrave;nh BT07-H7- TT1 (Khu biệt thự Star Lake) cũng như cập nhật những thiết kế mới nhất của RoyalHome nh&eacute;!</p>\r\n', 'struc_img/quytrinh.jpg', '2018-12-12', ''),
('KT_03', 17, 'RoyalHome cùng bộ sưu tập thiết kế nội thất tân cổ điển tinh tế, sang trọng lên ngôi năm 2019', '<p><em>Thiết kế nội thất t&acirc;n cổ điển l&agrave; phong c&aacute;ch được nhiều người lựa chọn trong thời gian gần đ&acirc;y. Kiến tr&uacute;c t&acirc;n cổ điển mang tới c&aacute;i nh&igrave;n gợi cảm, tuy giản đơn nhưng đem lại sự tinh tế, thanh lịch cho kh&ocirc;ng gian m&agrave; n&oacute; hiện diện. Giống như sự chắt lọc tinh t&uacute;y từ qu&aacute; khứ, cộng th&ecirc;m n&eacute;t ph&oacute;ng kho&aacute;ng của con người hiện đại, t&acirc;n cổ điển v&igrave; thế lu&ocirc;n c&oacute; được sức sống bền bỉ với những gi&aacute; trị cao về nghệ thuật v&agrave; thẩm mỹ.</em></p>\r\n\r\n<p><img style=\"width:100%;\" alt=\"\" src=\"https://thietkeroyalhome.com/upload_images/images/tang1-khachbep7_1552114366.jpg\" /></p>\r\n\r\n<p>Trong thiết kế nội thất t&acirc;n cổ điển, c&aacute;c Kiến tr&uacute;c sư của RoyalHome thường tập trung v&agrave;o c&aacute;c bức tường, đặc trưng l&agrave; sự ngăn chia kh&ocirc;ng gian bởi c&aacute;c chi tiết ph&agrave;o, chỉ tạo n&ecirc;n c&aacute;c mảng miếng. Đồng thời c&aacute;c mảng miếng cũng ngăn chia kh&ocirc;ng gian nội thất th&agrave;nh c&aacute;c khu, tương ứng với mảng trần v&agrave; mảng tường xung quanh. &nbsp;</p>\r\n\r\n<p><img style=\"width:100%;\" alt=\"\" src=\"https://thietkeroyalhome.com/upload_images/images/%C3%A1%C2%BA%C2%A3nh/tang1-khachbep3_1552114745_jpg%20(1).jpg\" /></p>\r\n\r\n<p>RoyalHome thường hay ch&uacute; &yacute; đến tỷ lệ v&agrave;ng khi ngăn chia c&aacute;c &ocirc;, c&aacute;c mảng. Đ&acirc;y vốn l&agrave; ch&igrave;a kh&oacute;a của vẻ đẹp v&agrave; nghệ thuật, mang tới c&aacute;i nh&igrave;n h&agrave;i h&ograve;a. Với một kh&ocirc;ng gian được thiết kế theo tỷ lệ v&agrave;ng, d&ugrave; rằng người xem kh&ocirc;ng hiểu nhiều về kiến tr&uacute;c, vẫn lu&ocirc;n bị thu h&uacute;t một c&aacute;ch rất tự nhi&ecirc;n.</p>\r\n\r\n<p><img style=\"width:100%;\" alt=\"\" src=\"https://thietkeroyalhome.com/upload_images/images/2018/anhbac-phongbep26_1553073166.jpg\" /></p>\r\n\r\n<p>Hy vọng RoyalHome c&ugrave;ng bộ sưu tập thiết kế nội thất t&acirc;n cổ điển tinh tế, sang trọng l&ecirc;n ng&ocirc;i năm 2019 sẽ c&agrave;ng l&agrave;m tăng th&ecirc;m t&igrave;nh y&ecirc;u của qu&yacute; kh&aacute;ch h&agrave;ng d&agrave;nh cho phong c&aacute;ch t&acirc;n cổ điển.&nbsp;</p>\r\n', 'struc_img/kt2.jpg', '2018-01-18', ''),
('KT_04', 17, 'Lời khuyên từ Kiến trúc sư chuyên thiết kế nội thất', '<p><em>Thiết kế nội thất tưởng chừng như đơn giản, dễ d&agrave;ng nhưng lại c&oacute; rất nhiều kiến tr&uacute;c sư chưa đủ kinh nghiệm hay những người thiết kế nội thất lu&ocirc;n mắc phải những sai lầm kh&ocirc;ng đ&aacute;ng c&oacute;. C&ugrave;ng tham khảo b&agrave;i viết sau để nghe được những lời khuy&ecirc;n bổ &iacute;ch nhất từ Kiến tr&uacute;c sư chuy&ecirc;n thiết kế nội thất.</em></p>\r\n\r\n<p><img style=\"width:100%;\" alt=\"\" src=\"https://thietkeroyalhome.com/upload_images/images/20476101_1496113640464290_6590871196715149987_n.jpg\" /></p>\r\n\r\n<p><strong>Khi thiết kế nội thất n&ecirc;n sử dụng những gam m&agrave;u đen trắng đối lập hoặc gam m&agrave;u trung t&iacute;nh, nhẹ nh&agrave;ng</strong></p>\r\n\r\n<p>Chọn lựa m&agrave;u sắc l&agrave; v&ocirc; c&ugrave;ng quan trọng trong thiết kế nội thất, d&ugrave; l&agrave; ở biệt thự, chung cư, nh&agrave; phố hay trung t&acirc;m thương mại. Việc chọn gam m&agrave;u trung t&iacute;nh hay nhẹ nh&agrave;ng sẽ gi&uacute;p bạn phối m&agrave;u dễ d&agrave;ng hơn. Đặc biệt nếu phối với m&agrave;u s&aacute;ng sẽ gi&uacute;p căn ph&ograve;ng trở n&ecirc;n tho&aacute;ng m&aacute;t v&agrave; rộng r&atilde;i hơn bao giờ hết.</p>\r\n\r\n<p>Theo c&aacute;c chuy&ecirc;n gia về phong thủy th&igrave; với những gam m&agrave;u trung t&iacute;nh sẽ c&oacute; t&aacute;c dụng tạo sự ấm &aacute;p, y&ecirc;n b&igrave;nh, h&acirc;m n&oacute;ng t&igrave;nh cảm, hạnh ph&uacute;c gia đ&igrave;nh.</p>\r\n\r\n<p>Ngo&agrave;i ra đen trắng l&agrave; hai m&agrave;u sắc tương phản nhau, tuy nhi&ecirc;n nếu được kết hợp với nhau trong kh&ocirc;ng gian nội thất sẽ gi&uacute;p cho căn hộ được nổi bật hẳn l&ecirc;n.</p>\r\n\r\n<p>Lưu &yacute; kh&ocirc;ng n&ecirc;n sử dụng qu&aacute; nhiều m&agrave;u sắc, điều đ&oacute; sẽ khiến căn hộ trở n&ecirc;n thật rườm r&agrave;, rối mắt, tạo cảm gi&aacute;c kh&oacute; chịu cho người nh&igrave;n. Việc sử dụng qu&aacute; nhiều m&agrave;u sắc như thế sẽ ảnh hưởng xấu đến thẩm mỹ của căn hộ, mang đến cảm gi&aacute;c n&oacute;ng bức hơn nhất l&agrave; với những căn hộ c&oacute; diện t&iacute;ch nhỏ.</p>\r\n\r\n<p><img style=\"width:100%;\" alt=\"\" src=\"https://thietkeroyalhome.com/upload_images/images/27331685_1668103579931961_1355278368393828876_n.jpg\" /></p>\r\n\r\n<p><strong>L&ecirc;n danh s&aacute;ch những đồ nội thất cần thiết trước khi thiết kế</strong></p>\r\n\r\n<p>Yếu tố quan trọng nhất để tạo n&ecirc;n một kh&ocirc;ng gian nội thất đẹp đ&oacute; ch&iacute;nh l&agrave; t&iacute;nh tiện nghi, gi&aacute; trị thẩm mĩ, c&aacute;ch sắp xếp, bố tr&iacute; c&aacute;c mảng kh&ocirc;ng gian v&agrave; phối hợp m&agrave;u sắc h&agrave;i h&ograve;a.</p>\r\n\r\n<p>Mỗi một kh&ocirc;ng gian đều c&oacute; n&eacute;t đặc trưng v&agrave; chức năng ri&ecirc;ng. V&igrave; vậy, trong qu&aacute; tr&igrave;nh thiết kế, gia chủ cần phải t&iacute;nh to&aacute;n tỷ lệ b&agrave;y tr&iacute; giữa c&aacute;c kh&ocirc;ng gian với nhau. N&ecirc;n c&acirc;n nhắc xem đối với mỗi kh&ocirc;ng gian th&igrave; ưu ti&ecirc;n c&aacute;i g&igrave;, lược bỏ c&aacute;i g&igrave;, điều g&igrave; l&agrave; điểm nhấn nổi bật cho kh&ocirc;ng gian đ&oacute;,&hellip;</p>\r\n\r\n<p>Nhiều gia chủ chỉ v&igrave; đ&atilde; kh&ocirc;ng c&oacute; sự t&iacute;nh to&aacute;n, chuẩn bị kỹ lưỡng n&ecirc;n đ&atilde; mua qu&aacute; nhiều đồ nội thất kh&ocirc;ng cần thiết hay để cho c&aacute;c b&ecirc;n thiết kế thi c&ocirc;ng tự vẽ ra qu&aacute; nhiều đồ đạc chẳng để l&agrave;m g&igrave; cả. Điều đ&oacute; vừa khiến cho kh&ocirc;ng gian nội thất của căn hộ bị chật chội, rườm r&agrave; m&agrave; c&ograve;n khiến gia chủ mất th&ecirc;m một khoản chi ph&iacute; kh&ocirc;ng hề nhỏ m&agrave; c&ograve;n thật sự rất l&atilde;ng ph&iacute;.</p>\r\n\r\n<p><strong>Đảm bảo đầy đủ c&aacute;c yếu tố phong thủy trong thiết kế nội thất nh&agrave; ở</strong></p>\r\n\r\n<p>Phong thủy l&agrave; yếu tố lu&ocirc;n cần được đảm bảo trong thiết kế, thi c&ocirc;ng bất kỳ hạng mục, c&ocirc;ng tr&igrave;nh nh&agrave; ở n&agrave;o. Sự h&ograve;a hợp về mặt phong thủy kh&ocirc;ng chỉ g&oacute;p phần l&agrave;m gia tăng t&iacute;nh thẩm mĩ, sự c&acirc;n đối trong kh&ocirc;ng gian nh&agrave; ở m&agrave; c&ograve;n ảnh hưởng đến t&acirc;m trạng, vận may của gia chủ.</p>\r\n\r\n<p><strong>Sử dụng những đồ vật c&oacute; t&iacute;nh đa năng</strong></p>\r\n\r\n<p>Khi thiết kế nội thất chung cư nhỏ, sử dụng những đồ vật c&oacute; t&iacute;nh đa năng l&agrave; một c&aacute;ch thiết kế v&ocirc; c&ugrave;ng th&ocirc;ng minh. Kh&ocirc;ng những gi&uacute;p bạn tiết kiệm được kh&ocirc;ng gian, m&agrave; c&ograve;n v&ocirc; c&ugrave;ng ngăn nắp, tiện lợi.</p>\r\n\r\n<p><img style=\"width:100%;\" alt=\"\" src=\"https://thietkeroyalhome.com/upload_images/images/tang-1-3_1552701056.jpg\" /></p>\r\n\r\n<p>C&aacute;c chuy&ecirc;n gia nội thất khuy&ecirc;n rằng, một kh&ocirc;ng gian nh&agrave; ở d&ugrave; c&oacute; s&aacute;ng tạo đến mức n&agrave;o th&igrave; cũng kh&ocirc;ng được bỏ qua quy tắc &ldquo;h&ograve;a hợp&rdquo;. Sự h&ograve;a hợp ở đ&acirc;y ch&iacute;nh l&agrave; sự c&acirc;n bằng về m&agrave;u sắc, thống nhất về bố cục v&agrave; sự li&ecirc;n kết giữa c&aacute;c vị tr&iacute; kh&ocirc;ng gian kh&aacute;c nhau trong nh&agrave;.</p>\r\n\r\n<p>Mỗi một kh&ocirc;ng gian đều c&oacute; n&eacute;t đặc trưng v&agrave; chức năng ri&ecirc;ng, v&igrave; vậy gia chủ n&ecirc;n chọn c&aacute;c loại vật dụng vừa c&oacute; t&iacute;nh ứng dụng, vừa c&oacute; t&iacute;nh nghệ thuật l&agrave;m vật trang tr&iacute; khi thiết kế nội thất l&agrave; rất ph&ugrave; hợp. Với sự kết hợp 2 trong 1 n&agrave;y sẽ gi&uacute;p gia chủ tạo n&ecirc;n những mảng kh&ocirc;ng gian sống tiện nghi, sang trọng v&agrave; tinh lược hết mức.</p>\r\n\r\n<p>Với những lời khuy&ecirc;n tr&ecirc;n đ&acirc;y từ đội ngũ kiến tr&uacute;c sư chuy&ecirc;n nghiệp đến từ RoyalHome, hy vọng sẽ mang đến cho qu&yacute; kh&aacute;ch h&agrave;ng những th&ocirc;ng tin bổ &iacute;ch nhất.</p>\r\n', 'struc_img/kt4.jpg', '2019-10-22', ''),
('KT_05', 17, 'Phối màu không gian nội thất phù hợp với cá tính', '<p><em>Khi lựa chọn m&agrave;u sắc cho ng&ocirc;i nh&agrave;, bạn h&atilde;y c&acirc;n nhắc l&agrave;m sao để c&oacute; thể cảm nhận được t&acirc;m trạng cũng như &yacute; nghĩa của từng bảng m&agrave;u. Đ&acirc;u l&agrave; m&agrave;u sắc đại diện, đ&acirc;u l&agrave; t&acirc;m trạng m&agrave; m&agrave;u sắc sẽ gợi l&ecirc;n. M&agrave;u sắc trong thiết kế nội thất c&oacute; ảnh hưởng đ&aacute;ng kể cho cả tinh thần v&agrave; thể chất. Dưới đ&acirc;y l&agrave; những v&iacute; dụ kh&aacute;c nhau về m&agrave;u sắc. V&agrave; chia sẻ c&aacute;ch l&agrave;m thế n&agrave;o để sử dụng hiệu quả những bảng m&agrave;u, đồng thời thể hiện được những &yacute; tưởng v&agrave; gợi ra cảm x&uacute;c.</em></p>\r\n\r\n<p><img style=\"width:100%;\" alt=\"\" src=\"https://thietkeroyalhome.com/upload_images/images/%C3%A1%C2%BA%C2%A3nh/k4_1553733043.jpg\" /></p>\r\n\r\n<p><strong>Vai tr&ograve; của việc phối m&agrave;u trong kh&ocirc;ng gian nội thất</strong></p>\r\n\r\n<p>H&atilde;y sử dụng c&aacute;c t&ocirc;ng m&agrave;u s&aacute;ng để l&agrave;m nổi bật một t&iacute;nh năng, t&ocirc;ng m&agrave;u tối nếu muốn nội thất đ&oacute; như kh&ocirc;ng được hiển thị. C&ugrave;ng một m&agrave;u sắc nhưng với cường độ t&ocirc;ng m&agrave;u kh&aacute;c nhau cũng mang về h&agrave;ng loạt t&acirc;m trạng v&agrave; nhận thức kh&aacute;c nhau. Ngo&agrave;i ra m&agrave;u sắc c&oacute; thể truyền đạt cảm x&uacute;c kh&aacute;c nhau hoặc &yacute; nghĩa phụ thuộc v&agrave;o văn h&oacute;a, giới t&iacute;nh hay độ tuổi.</p>\r\n\r\n<p>Những m&agrave;u sắc đậm, tươi s&aacute;ng hay pastel nhẹ nh&agrave;ng để c&oacute; thể đưa v&agrave;o kh&ocirc;ng gian trung t&iacute;nh, nhưng phải đảm bảo ch&uacute;ng kh&ocirc;ng bị phai mờ. Gam m&agrave;u trung t&iacute;nh cũng c&oacute; thể sử dụng linh hoạt như một điểm nhấn hay phụ kiện.</p>\r\n\r\n<p>Một m&agrave;u trung t&iacute;nh chủ đạo dường như xuất hiện trong kh&ocirc;ng gian hiện đại, v&agrave; c&aacute;c bảng m&agrave;u đậm sẽ trở th&agrave;nh bảng trang tr&iacute; ấn tượng. Để tạo n&ecirc;n một kh&ocirc;ng gian nội thất m&agrave;u x&aacute;m th&agrave;nh c&ocirc;ng đ&ograve;i hỏi phải c&oacute; sự pha trộn h&agrave;i h&ograve;a của nhiều chủ đề m&agrave;u sắc kh&aacute;c nhau.</p>\r\n\r\n<p><img style=\"width:100%;\" alt=\"\" src=\"https://thietkeroyalhome.com/upload_images/images/%C3%A1%C2%BA%C2%A3nh/340a5164-copy.jpg\" /></p>\r\n\r\n<p><strong>&Yacute; nghĩa của những m&agrave;u sắc thường được sử dụng trong thiết kế nội thất</strong></p>\r\n\r\n<p><strong><em>M&agrave;u trắng</em></strong></p>\r\n\r\n<p>M&agrave;u trắng đại diện cho sự tinh khiết, đơn giản v&agrave; sạch sẽ. Ngo&agrave;i ra kh&ocirc;ng gian m&agrave;u trắng sẽ gi&uacute;p cho những m&oacute;n nội thất được hiển thị r&otilde; hơn v&agrave; dễ d&agrave;ng kết hợp với những m&agrave;u sắc kh&aacute;c.V&agrave; đặc biệt với những kh&ocirc;ng gian nhỏ, nếu chọn trắng s&aacute;ng l&agrave;m chủ đạo sẽ gi&uacute;p kh&ocirc;ng gian th&ocirc;ng tho&aacute;ng v&agrave; tăng cường &aacute;nh s&aacute;ng tốt hơn.</p>\r\n\r\n<p>Khi sắc trắng được lựa chọn l&agrave;m lược đồ m&agrave;u ch&iacute;nh, th&igrave; c&aacute;c yếu tố như h&igrave;nh dạng, kết cấu, bề mặt v&agrave; những đường điểm nhấn nội thất cần phải được đặc biệt quan t&acirc;m. M&agrave;u trắng rất phổ biến trong nội thất v&igrave; n&oacute; nhẹ v&agrave; dễ d&agrave;ng ph&ugrave; hợp với mọi thứ. Tuy nhi&ecirc;n n&oacute; sẽ l&agrave;m lộ r&otilde; hơn những vết bẩn, do đ&oacute; sẽ kh&oacute; khăn so với c&aacute;c m&agrave;u sắc kh&aacute;c trong việc giữ sạch.</p>\r\n\r\n<p><strong><em>M&agrave;u đen</em></strong></p>\r\n\r\n<p>M&agrave;u đen sẽ gợi l&ecirc;n sự tinh tế với sức mạnh v&agrave; quyền lực. M&agrave;u đen c&oacute; thể th&ecirc;m chiều s&acirc;u cho kh&ocirc;ng gian, mang đến một cảm gi&aacute;c phong ph&uacute; v&agrave; kịch t&iacute;nh hơn. Nếu sở hữu một căn ph&ograve;ng nhạt nhẽo th&igrave; th&ecirc;m v&agrave;o một v&agrave;i chi tiết m&agrave;u đen l&agrave; rất cần thiết. Một số nh&agrave; thiết kế sẽ ch&uacute; &yacute; đến việc tạo điểm nhấn kh&ocirc;ng gian với những yếu tố sử dụng sắc đen.</p>\r\n\r\n<p><strong><em>Những gam m&agrave;u trung t&iacute;nh kh&aacute;c</em></strong></p>\r\n\r\n<p>M&agrave;u x&aacute;m sẽ mang một giai điệu ấm &aacute;p v&agrave; m&aacute;t mẻ v&agrave;o kh&ocirc;ng gian. Đ&acirc;y c&oacute; lẽ l&agrave;m m&agrave;u sắc phổ biến nhất trong xu hướng nội thất ng&agrave;y nay. Sắc th&aacute;i của m&agrave;u x&aacute;m truyền đạt một cảm gi&aacute;c m&aacute;t mẻ v&agrave; sắc n&eacute;t. M&agrave;u x&aacute;m tượng trưng cho người sở hữu một tinh thần điềm đạm.</p>\r\n\r\n<p>M&agrave;u be mang về sự ấm &aacute;p v&agrave; gần gũi. Đặc biệt với c&aacute;c kh&ocirc;ng gian thư gi&atilde;n v&agrave; nghỉ ngơi th&igrave; đ&acirc;y sẽ l&agrave; lựa chọn tuyệt vời cho những gi&acirc;y ph&uacute;t bạn cần sự thư th&aacute;i. Nếu như y&ecirc;u th&iacute;ch một bầu kh&ocirc;ng kh&iacute; nhiệt đới hay kh&ocirc;ng gian đầy c&aacute;t ven biển th&igrave; m&agrave;u be sẽ l&agrave;m rất tốt trong việc n&agrave;y.</p>\r\n\r\n<p>N&acirc;u đậm l&agrave; một bảng m&agrave;u mang lại sức mạnh ho&agrave;n hảo. C&aacute;c bức tường m&agrave;u n&acirc;u cho một kh&ocirc;ng gian như được bao bọc an to&agrave;n v&agrave; đặc biệt hiệu quả trong việc mang về một bầu kh&ocirc;ng kh&iacute; th&acirc;n mật v&agrave; ấm c&uacute;ng.</p>\r\n\r\n<p><img style=\"width:100%;\" alt=\"\" src=\"https://thietkeroyalhome.com/upload_images/images/%C3%A1%C2%BA%C2%A3nh/n1.jpg\" /></p>\r\n\r\n<p><strong><em>M&agrave;u xanh da trời</em></strong></p>\r\n\r\n<p>Xanh da trời sẽ gợi l&ecirc;n cảm gi&aacute;c b&igrave;nh y&ecirc;n v&agrave; tĩnh lặng. Sắc m&agrave;u n&agrave;y như một bầu trời xanh dịu nhẹ hay d&ograve;ng nước thanh m&aacute;t, mang lại sự nhẹ nh&agrave;ng đặc biệt hơn cho cuộc sống hằng ng&agrave;y.</p>\r\n\r\n<p><em><strong>M&agrave;u xanh l&aacute; c&acirc;y</strong></em></p>\r\n\r\n<p>M&agrave;u xanh l&aacute; l&agrave; đại diện cho thi&ecirc;n nhi&ecirc;n tươi mới, hay sự t&aacute;i tạo một sắc th&aacute;i mới mẻ. M&agrave;u xanh khuyến kh&iacute;ch cho một khởi đầu mới. C&aacute;c yếu tố nối thất m&agrave;u xanh được coi như rất thư gi&atilde;n v&agrave; &ecirc;m đềm. C&aacute;ch hiệu quả nhất để đưa m&agrave;u xanh v&agrave;o kh&ocirc;ng gian đ&oacute; ch&iacute;nh l&agrave; bổ sung c&acirc;y xanh.</p>\r\n\r\n<p>C&oacute; thể kh&ocirc;ng được ưu &aacute;i để chọn l&agrave;m m&agrave;u sắc chủ đạo nhưng sắc xanh l&agrave; sẽ kh&ocirc;ng thể thiếu trong việc trang tr&iacute; kh&ocirc;ng gian nội thất.</p>\r\n\r\n<p><strong><em>M&agrave;u đỏ</em></strong></p>\r\n\r\n<p>M&agrave;u đỏ được ca ngợi l&agrave; sắc m&agrave;u của quyền lực v&agrave; đam m&ecirc;. M&agrave;u đỏ cho mọi người cảm thấy mạnh mẽ v&agrave; tự tin. Nhưng việc qu&aacute; l&agrave;m dụng cũng c&oacute; thể g&acirc;y n&ecirc;n sự căng thẳng v&agrave; lo lắng nếu tiếp x&uacute;c qu&aacute; l&acirc;u. Ch&iacute;nh v&igrave; vậy m&agrave; m&agrave;u đỏ chỉ được c&acirc;n nhắc để tạo n&ecirc;n điểm nhấn, v&agrave; đặc biệt hạn chế sử dụng h&agrave;ng loạt trong những khu vực sinh hoạt thường xuy&ecirc;n như ph&ograve;ng kh&aacute;ch, ph&ograve;ng ngủ.</p>\r\n\r\n<p>Đ&acirc;y kh&ocirc;ng phải l&agrave; lựa chọn hay cho nh&agrave; bếp nhưng sẽ l&agrave; một lựa chọn tuyệt vời cho khu vực ăn uống v&igrave; n&oacute; k&iacute;ch th&iacute;ch sự th&egrave;m ăn.</p>\r\n\r\n<p><em><strong>M&agrave;u hồng</strong></em></p>\r\n\r\n<p>M&agrave;u hồng đ&atilde; trở n&ecirc;n phổ biến. N&oacute; tượng trưng cho tuổi trẻ vui tươi v&agrave; ngọt ng&agrave;o. Kh&ocirc;ng gian sẽ trở n&ecirc;n nữ t&iacute;nh với sự nhẹ nh&agrave;ng v&agrave; thoải m&aacute;i. Kh&ocirc;ng gian th&iacute;ch hợp nhất cho m&agrave;u sắc n&agrave;y đ&oacute; ch&iacute;nh l&agrave; ph&ograve;ng ngủ.</p>\r\n\r\n<p><img style=\"width:100%;\" alt=\"\" src=\"https://thietkeroyalhome.com/upload_images/images/%C3%A1%C2%BA%C2%A3nh/340a5177-copy.jpg\" /></p>\r\n\r\n<p>Hy vọng với những chia sẻ tr&ecirc;n đ&acirc;y sẽ gi&uacute;p c&aacute;c bạn c&oacute; th&ecirc;m những kiến thức bổ &iacute;ch nhất trong thiết kế nội thất.</p>\r\n', 'struc_img/kt5.jpg', '2019-08-21', ''),
('KT_06', 17, 'Tự thiết kế nội thất biệt thự - Có nên không?', '<p>Hiện nay, rất nhiều người đang chọn c&aacute;ch tự thiết kế nội thất với suy nghĩ đơn giản chỉ l&agrave; việc đi mua đồ nội thất thật &ldquo;đắt&rdquo;, thật &ldquo;xịn&rdquo; theo sở th&iacute;ch của m&igrave;nh, lựa chọn những đồ m&agrave; m&igrave;nh &ldquo;cảm thấy&rsquo; ph&ugrave; hợp rồi đặt v&agrave;o l&agrave; xong. Nhưng sự thật, c&ocirc;ng việc n&agrave;y phức tạp hơn như thế v&agrave; để c&oacute; một kh&ocirc;ng gian sống khoa học, mang t&iacute;nh thẩm mỹ bạn n&ecirc;n sử dụng sự trợ gi&uacute;p từ c&aacute;c kiến tr&uacute;c sư nhất l&agrave; khi kh&ocirc;ng gian của bạn l&agrave; một căn biệt thự với những y&ecirc;u cầu phức tạp cũng như đẳng cấp của c&ocirc;ng tr&igrave;nh.</p>\r\n\r\n<p><img alt=\"\" src=\"http://royalhome.shopbanhang.vn/demo2/wp-content/uploads/2017/05/tai-sao-can-thue-thiet-ke-noi-that-biet-thu-1.jpg\" />Dưới đ&acirc;y l&agrave; những điều m&agrave; c&aacute;c kiến tr&uacute;c sư sẽ đảm bảo mang đến cho c&ocirc;ng tr&igrave;nh của bạn.</p>\r\n\r\n<p><strong>1. C&ocirc;ng năng hợp l&yacute;</strong></p>\r\n\r\n<p>Một căn biệt thự với rất nhiều kh&ocirc;ng gian kh&aacute;c nhau đ&ograve;i hỏi sự sắp sếp một c&aacute;ch khoa học sao cho tiện nghi m&agrave; kh&ocirc;ng c&oacute; sự chồng ch&eacute;o, dư thừa c&ocirc;ng năng kh&ocirc;ng cần thiết. Nếu như kh&ocirc;ng c&oacute; một bản thiết kế nội thất bạn rất dễ rơi v&agrave;o t&igrave;nh trạng mua rất nhiều nội thất m&agrave; vẫn kh&ocirc;ng &lsquo;đủ d&ugrave;ng&rdquo; hoặc sự thừa thiếu những c&ocirc;ng năng trong một kh&ocirc;ng gian nhất định.</p>\r\n\r\n<p><strong>2. Phong c&aacute;ch ph&ugrave; hợp v&agrave; thống nhất</strong></p>\r\n\r\n<p>Mỗi người, mỗi gia đ&igrave;nh c&oacute; phong c&aacute;ch sống sống kh&aacute;c nhau v&agrave; do đ&oacute; sẽ c&oacute; những phong c&aacute;ch ph&ugrave; hợp nhất định. V&agrave; kiến tr&uacute;c sư với những kiến thức chuy&ecirc;n m&ocirc;n, kinh nghiệm sẽ l&agrave; những người gi&uacute;p bạn t&igrave;m ra phong c&aacute;ch ph&ugrave; hợp đ&oacute;. Ngay cả khi bạn đ&atilde; lựa chọn được một phong c&aacute;ch ph&ugrave; hợp th&igrave; việc lựa chọn nội thất thống nhất theo style đ&oacute; cũng l&agrave; một việc rất kh&oacute; khăn khi bạn phải quan t&acirc;m đến tất cả c&aacute;c vấn đề như kiểu d&aacute;ng, m&agrave;u sắc v&agrave; cả những họa tiết trang tr&iacute; d&ugrave; l&agrave; nhỏ nhất. Nhưng dưới b&agrave;n tay của c&aacute;c kiến tr&uacute;c sư th&igrave; việc n&agrave;y lại trở n&ecirc;n v&ocirc; c&ugrave;ng đơn giản.</p>\r\n\r\n<p><strong><img alt=\"\" src=\"http://royalhome.shopbanhang.vn/demo2/wp-content/uploads/2017/05/tai-sao-can-thue-thiet-ke-noi-that-biet-thu-2.jpg\" />3. T&iacute;nh c&aacute; nh&acirc;n h&oacute;a</strong></p>\r\n\r\n<p>Những cửa h&agrave;ng, showroom nội thất c&oacute; mặt khắp mọi nơi v&agrave; bạn c&oacute; thể dễ d&agrave;ng c&oacute; được m&oacute;n đồ nội thất m&agrave; bạn muốn nhưng điều đ&oacute; cũng c&oacute; nghĩa l&agrave; những người kh&aacute;c cũng ho&agrave;n to&agrave;n c&oacute; thể c&oacute; m&oacute;n đồ tương tự v&agrave; căn biệt thự của bạn cũng sẽ giống như tất cả những căn hộ kh&aacute;c. Khi đ&atilde; c&oacute; một căn biệt thự th&igrave; việc khiến n&oacute; nhạt nh&ograve;a, kh&ocirc;ng dấu ấn hẳn kh&ocirc;ng phải mong muốn của bạn. C&aacute;c kiến tr&uacute;c sư với sự s&aacute;ng tạo, khả năng cập nhật những xu hướng mới sẽ gi&uacute;p bạn thiết kế, phối hợp c&aacute;c m&oacute;n đồ nội thất tạo n&ecirc;n một kh&ocirc;ng gian độc nhất v&ocirc; nhị, mang dấu ấn ri&ecirc;ng của bạn, kh&ocirc;ng bị trộn lẫn. Với tất cả những điều m&agrave; c&aacute;c kiến tr&uacute;c sư chắc chắn sẽ mang đến cho bạn, chắc hẳn bạn đ&atilde; nhận ra rằng việc lựa chọn một c&ocirc;ng ty thiết kế v&agrave; thi c&ocirc;ng nội thất gi&uacute;p đỡ bạn trong qu&aacute; tr&igrave;nh thiết kế căn hộ của m&igrave;nh l&agrave; một quyết định ho&agrave;n to&agrave;n đ&uacute;ng đắn. H&atilde;y đến với RoyalHome Việt Nam - Ch&uacute;ng t&ocirc;i sẽ kh&ocirc;ng l&agrave;m bạn phải thất vọng. Tất cả những mong muốn, ước mơ của bạn về căn hộ l&yacute; tưởng sẽ được RoyalHome hiện thực h&oacute;a một c&aacute;ch ho&agrave;n hảo nhất!&nbsp;<strong>THIẾT KẾ NỘI THẤT ROYALHOME VIỆT NAM</strong></p>\r\n\r\n<ul>\r\n	<li>Địa chỉ: Số 15 LK6A - KĐT L&agrave;ng Việt Kiều Ch&acirc;u &Acirc;u - Mỗ Lao - H&agrave; Đ&ocirc;ng - H&agrave; Nội</li>\r\n	<li>Điện thoại: 09 666 555 00 - 09 757 078 58</li>\r\n	<li>Email: thietkeroyalhome@gmail.com</li>\r\n	<li>Websie: www.thietkeroyalhome.com</li>\r\n</ul>\r\n', 'struc_img/kt4.jpg', '0000-00-00', ''),
('TT_01', 16, 'Nghệ thuật thiết kế ánh sáng tự nhiên cho không gian nhỏ', '<h2>&Aacute;nh s&aacute;ng l&agrave; một trong những yếu tố hết sức quan trọng khi thiết kế nội thất. Đặc biệt, đối với những kh&ocirc;ng gian c&oacute; diện t&iacute;ch nhỏ hẹp, l&agrave;m thế n&agrave;o để tận dụng tối đa &aacute;nh s&aacute;ng tự nhi&ecirc;n, đem lại cảm gi&aacute;c rộng r&atilde;i, thoải m&aacute;i? B&agrave;i viết dưới đ&acirc;y sẽ cung cấp cho bạn những th&ocirc;ng tin hữu &iacute;ch.&nbsp;</h2>\r\n\r\n<h2>Sử dụng giếng trời</h2>\r\n\r\n<p>C&aacute;ch thiết kế &aacute;nh s&aacute;ng tự nhi&ecirc;n hiệu quả cho những căn nh&agrave; c&oacute; diện t&iacute;ch nhỏ l&agrave; sử dụng giếng trời. Tại m&aacute;i nh&agrave;, bạn để một khoảng kh&ocirc;ng gian trống v&agrave; lợp bằng k&iacute;nh cường lực trong suốt. Nhờ đ&oacute;, &aacute;nh s&aacute;ng c&oacute; thể lấy được nhiều v&agrave;o ban ng&agrave;y v&agrave; chiếu s&aacute;ng cho cả ng&ocirc;i nh&agrave;.&nbsp;</p>\r\n\r\n<p>Giếng trời c&oacute; thể đặt tại c&aacute;c vị tr&iacute; như cầu thang, khu trung t&acirc;m hay ph&iacute;a sau ng&ocirc;i nh&agrave;. B&ecirc;n cạnh đ&oacute;, bạn cũng cần t&iacute;nh to&aacute;n độ lớn của giếng trời sao cho ph&ugrave; hợp với diện t&iacute;ch ng&ocirc;i nh&agrave; để đem lại hiệu quả chiếu s&aacute;ng tốt nhất. Nếu ng&ocirc;i nh&agrave; c&oacute; diện t&iacute;ch c&agrave;ng lớn th&igrave; cần tăng khoảng rộng của giếng trời v&agrave; ngược lại.&nbsp;</p>\r\n\r\n<h2>Mở rộng c&aacute;c khung cửa</h2>\r\n\r\n<p><img style=\"width:100%;\" alt=\"\" src=\"https://thietkeroyalhome.com/images/projects/2019/03/19/original/penhouse-chihoan-phongkhach12-2_1552975431.jpg\" style=\"height:700px; width:1400px\" /></p>\r\n\r\n<p>Khi thiết kế nội thất cho ng&ocirc;i nh&agrave;, nếu khung cửa được thiết kế c&agrave;ng rộng th&igrave; &aacute;nh s&aacute;ng tự nhi&ecirc;n c&agrave;ng dễ d&agrave;ng tr&agrave;n v&agrave;o nh&agrave; hơn. Do đ&oacute;, để tận dụng &aacute;nh s&aacute;ng tự nhi&ecirc;n cho kh&ocirc;ng gian nhỏ trở n&ecirc;n rộng r&atilde;i, tho&aacute;ng đ&atilde;ng hơn, bạn n&ecirc;n mở rộng khung cửa sổ, cửa ch&iacute;nh ng&ocirc;i nh&agrave;.</p>\r\n\r\n<p>Đặc biệt, những vị tr&iacute; cửa đ&oacute;n nắng, gi&oacute; n&ecirc;n được ưu ti&ecirc;n l&agrave;m khung cửa lớn. Hệ thống cửa k&iacute;nh gi&uacute;p đ&oacute;n &aacute;nh s&aacute;ng, đem lại cho bạn cảm gi&aacute;c thoải m&aacute;i, h&ograve;a m&igrave;nh c&ugrave;ng thi&ecirc;n nhi&ecirc;n. Đồng thời, bạn c&oacute; thể kết hợp sử dụng r&egrave;m treo để dễ d&agrave;ng điều chỉnh &aacute;nh s&aacute;ng cho ph&ugrave; hợp.&nbsp;</p>\r\n\r\n<h2>Thiết kế th&ocirc;ng nhau giữa c&aacute;c ph&ograve;ng</h2>\r\n\r\n<p><img style=\"width:100%;\" alt=\"\" src=\"https://thietkeroyalhome.com/images/projects/2019/04/16/original/2_1555444313.jpg\" style=\"height:900px; width:1800px\" /></p>\r\n\r\n<p>Bạn c&oacute; thể thiết kế th&ocirc;ng nhau giữa c&aacute;c kh&ocirc;ng gian như ph&ograve;ng kh&aacute;ch, nh&agrave; bếp. Việc kh&ocirc;ng c&oacute; v&aacute;ch ngăn sẽ gi&uacute;p cho kh&ocirc;ng gian được rộng r&atilde;i đồng thời mang lại hiệu quả chiếu s&aacute;ng tốt hơn. Đặc biệt, đối với những ng&ocirc;i nh&agrave; c&oacute; kh&ocirc;ng gian nhỏ hẹp, việc thiết kế n&agrave;y sẽ gi&uacute;p hiệu quả tối ưu diện t&iacute;ch. Tầm nh&igrave;n th&ocirc;ng tho&aacute;ng v&agrave; &aacute;nh s&aacute;ng kh&ocirc;ng bị v&aacute;ch tường ngăn trở gi&uacute;p kh&ocirc;ng gian s&aacute;ng sủa hơn.&nbsp;</p>\r\n\r\n<h2>Sử dụng m&agrave;u sơn ph&ugrave; hợp</h2>\r\n\r\n<p><img style=\"width:100%;\" alt=\"\" src=\"https://thietkeroyalhome.com/images/projects/2019/03/19/original/01---khach-_1552979441.jpg\" style=\"height:600px; width:1200px\" /></p>\r\n\r\n<p>Với mong muốn căn ph&ograve;ng trở n&ecirc;n rộng r&atilde;i v&agrave; s&aacute;ng hơn, bạn n&ecirc;n tr&aacute;nh sử dụng những gam m&agrave;u trung t&iacute;nh, m&agrave;u tối cho tường trần. Ch&uacute;ng sẽ khiến kh&ocirc;ng gian trở n&ecirc;n chật chội, ngột ngạt. Những m&agrave;u sơn như trắng, v&agrave;ng kim sẽ l&agrave; sự lựa chọn th&iacute;ch hợp.&nbsp;</p>\r\n\r\n<p>M&agrave;u sắc nhẹ nh&agrave;ng, tinh tế gi&uacute;p bạn sở hữu một kh&ocirc;ng gian tươi mới v&agrave; s&aacute;ng sủa, tạo cảm gi&aacute;c l&uacute;c n&agrave;o cũng c&oacute; &aacute;nh s&aacute;ng tự nhi&ecirc;n trong nh&agrave;. B&ecirc;n cạnh đ&oacute;, bạn kết hợp thiết kế với &aacute;nh s&aacute;ng từ cửa sổ sẽ khiến kh&ocirc;ng gian sống trở n&ecirc;n lung linh v&agrave; tr&agrave;n đầy sức sống.&nbsp;</p>\r\n\r\n<p>Những kh&ocirc;ng gian nhỏ hẹp c&oacute; hạn chế về diện t&iacute;ch, tận dụng &aacute;nh s&aacute;ng tự nhi&ecirc;n sẽ gi&uacute;p căn nh&agrave; được rộng r&atilde;i v&agrave; tho&aacute;ng đ&atilde;ng hơn. Tr&ecirc;n đ&acirc;y l&agrave; những th&ocirc;ng tin cung cấp cho bạn về nghệ thuật thiết kế, sử dụng &aacute;nh s&aacute;ng tự nhi&ecirc;n. Hy vọng những th&ocirc;ng tin n&agrave;y hữu &iacute;ch cho bạn.&nbsp;</p>\r\n\r\n<p>Li&ecirc;n hệ ngay để được tư vấn đầy đủ những c&ocirc;ng tr&igrave;nh nội thất nh&agrave; ở tuyệt nhất, hợp phong thủy d&agrave;nh cho bạn!</p>\r\n\r\n<p>C&Ocirc;NG TY CP ROYALHOME VIỆT NAM</p>\r\n\r\n<p>➡ Văn ph&ograve;ng: Số 20LK6D &ndash; L&agrave;ng Việt Kiều Ch&acirc;u &Acirc;u &ndash; Mỗ Lao &ndash; H&agrave; Đ&ocirc;ng &ndash; H&agrave; Nội</p>\r\n\r\n<p>➡ Website: http://www.thietkeroyalhome.com</p>\r\n\r\n<p>☎ Hotline: 0966655500</p>\r\n\r\n<p>✉ Email: thietkeroyalhome@gmail.com</p>\r\n', 'struc_img/tt1.jpg', '2019-12-11', 'Ánh sáng là một trong những yếu tố hết sức quan trọng khi thiết kế...'),
('TT_02', 16, 'Các loại gạch ốp lát hoàn hảo cho không gian sống sang trọng, cao cấp', '<h2><em>Gạch ốp l&aacute;t l&agrave; một trong những yếu tố quan trọng tạo n&ecirc;n vẻ đẹp cuốn h&uacute;t v&agrave; kh&aacute;c biệt cho kh&ocirc;ng gian nội thất. Bạn mong muốn sở hữu một kh&ocirc;ng gian sống sang trọng v&agrave; đẳng cấp? Những loại gạch ốp l&aacute;t dưới đ&acirc;y sẽ l&agrave; sự lựa chọn ho&agrave;n hảo d&agrave;nh cho bạn.&nbsp;</em></h2>\r\n\r\n<h2>Gạch Granite</h2>\r\n\r\n<p><img style=\"width:100%;\" alt=\"\" src=\"https://thietkeroyalhome.com/images/projects/2019/03/09/original/tang1-khachbep7_1552114366.jpg\" style=\"height:900px; width:1800px\" /></p>\r\n\r\n<p>Gạch Granite hay c&ograve;n được gọi l&agrave; đ&aacute; hoa cương, với th&agrave;nh phần ch&iacute;nh l&agrave; Porcelain. Sản phẩm n&agrave;y c&oacute; rất nhiều ưu điểm nổi bật so với c&aacute;c loại gạch men truyền thống như độ cứng, chống thấm tốt, chịu lực cao, bền m&agrave;u,... B&ecirc;n n&oacute;, mẫu m&atilde; gạch cũng kh&aacute; đa dạng cho bạn lựa chọn, k&iacute;ch thước sản phẩm c&oacute; thể l&agrave; 60x60, 80x80, 60x120,... Loại gạch n&agrave;y c&oacute; thể sử dụng để l&aacute;t ở nhiều kh&ocirc;ng gian kh&aacute;c nhau như ph&ograve;ng tắm, ph&ograve;ng kh&aacute;ch, ban c&ocirc;ng,...&nbsp;</p>\r\n\r\n<h2>Gạch Big Slab</h2>\r\n\r\n<p>Đ&acirc;y l&agrave; loại gạch đang thịnh h&agrave;nh v&agrave; được ưa chuộng tại ch&acirc;u &Acirc;u, thường được sử dụng cho những kh&ocirc;ng gian sang trọng, đẳng cấp. Điểm đặc biệt của loại gạch n&agrave;y l&agrave; k&iacute;ch c&oacute; k&iacute;ch thước khổ lớn hơn rất nhiều so với những loại gạch kh&aacute;c. B&ecirc;n cạnh đ&oacute;, n&oacute; lại c&oacute; thể đ&aacute;p ứng đầy đủ những y&ecirc;u cầu khắt khe của c&aacute;c c&ocirc;ng tr&igrave;nh kiến tr&uacute;c.&nbsp;</p>\r\n\r\n<p><img style=\"width:100%;\" alt=\"\" src=\"https://thietkeroyalhome.com/images/projects/2019/03/09/original/tang1-phong-khach-bep2_1552120404.jpg\" style=\"height:700px; width:1400px\" /></p>\r\n\r\n<p>K&iacute;ch thước của gạch Big Slab thường l&agrave; 60x120, 80x160 hay 120x240. Với đặc điểm độ ngẫu nhi&ecirc;n của v&acirc;n gạch cao, sản phẩm n&agrave;y tạo n&ecirc;n vẻ đẹp nổi bật, ấn tượng cho cả kh&ocirc;ng gian nội thất khi sử dụng, đem lại nguồn cảm hứng tuyệt vời cho người d&ugrave;ng.&nbsp;</p>\r\n\r\n<p>Nhờ những ưu điểm nổi trội đ&oacute;, gạch Big Slab ng&agrave;y c&agrave;ng được sử dụng rộng r&atilde;i trong c&aacute;c kh&ocirc;ng gian rộng lớn như ph&ograve;ng kh&aacute;ch, sảnh ch&iacute;nh. Ngo&agrave;i ra, c&aacute;c kiến tr&uacute;c sư cũng c&oacute; thể vận dụng một c&aacute;ch tinh tế sản phẩm n&agrave;y trong thiết kế ph&ograve;ng tắm, ph&ograve;ng ngủ, tạo n&ecirc;n vẻ đẹp sang trọng, đẳng cấp.&nbsp;</p>\r\n\r\n<h2>Gạch thẻ</h2>\r\n\r\n<p><img style=\"width:100%;\" alt=\"\" src=\"https://thietkeroyalhome.com/images/projects/2019/03/19/original/3-pk-chivan-ecolife_1552959410.jpg\" style=\"height:700px; width:1400px\" /></p>\r\n\r\n<p>Gạch thẻ l&agrave; sản phẩm quen thuộc m&agrave; bạn c&oacute; thể bắt gặp rất nhiều tại c&aacute;c qu&aacute;n c&agrave; ph&ecirc;, nh&agrave; h&agrave;ng, ph&ograve;ng kh&aacute;ch,... Ch&uacute;ng tạo n&ecirc;n những điểm nhấn nổi bật v&agrave; thật sự ấn tượng cho kh&ocirc;ng gian nội thất. Việc sử dụng gạch thẻ đối với c&aacute;c căn hộ cao cấp l&agrave; một &yacute; tưởng rất tuyệt vời. N&oacute; gi&uacute;p kh&ocirc;ng gian sống của bạn v&agrave; gia đ&igrave;nh th&ecirc;m phần độc đ&aacute;o v&agrave; thu h&uacute;t.&nbsp;</p>\r\n\r\n<h2>Gạch giả gỗ</h2>\r\n\r\n<p>Gạch giả gỗ hay c&ograve;n được gọi l&agrave; gạch v&acirc;n gỗ l&agrave; một xu hướng được sử dụng phổ biến trong thiết kế v&agrave; thi c&ocirc;ng nội thất. Loại gạch n&agrave;y c&oacute; thể khắc phục được những nhược điểm của s&agrave;n gỗ tự nhi&ecirc;n như dễ bị bong, mốc khi ng&acirc;m nước, bị gi&atilde;n nở nếu sử dụng l&acirc;u, dễ trầy xước,...&nbsp;</p>\r\n\r\n<p><img style=\"width:100%;\" alt=\"\" src=\"https://thietkeroyalhome.com/images/projects/2019/03/28/original/k4_1553733043.jpg\" style=\"height:900px; width:1800px\" /></p>\r\n\r\n<p>Trong khi đ&oacute;, gạch v&acirc;n gỗ với phần xương gạch l&agrave;m từ porcelain, c&oacute; khả năng chịu nhiệt, chống thấm, chống trầy tốt. B&ecirc;n cạnh đ&oacute;, sản phẩm c&ograve;n &iacute;t bị chịu t&aacute;c động bởi m&ocirc;i trường b&ecirc;n ngo&agrave;i như nhiệt độ, độ ẩm,... n&ecirc;n c&oacute; độ bền cao. Ngo&agrave;i ra, k&iacute;ch thước của loại gạch n&agrave;y kh&aacute; đa dạng để lựa chọn như 15x60, 15x80, 15x90, 20x120cm.&nbsp;</p>\r\n\r\n<p>Gạch v&acirc;n gỗ sẽ l&agrave; lựa chọn tối ưu cho nhiều gia đ&igrave;nh, đặc biệt l&agrave; những gia đ&igrave;nh trẻ, năng động với chi ph&iacute; hợp l&yacute; hơn nhiều so với c&aacute;c loại s&agrave;n gỗ. Với nhiều kiểu v&acirc;n gỗ kh&aacute;c nhau, loại gạch n&agrave;y gi&uacute;p đem lại vẻ đẹp tươi mới, hiện đại cho kh&ocirc;ng gian nội thất nhưng đồng thời vẫn c&oacute; n&eacute;t mộc mạc, ấm &aacute;p tự nhi&ecirc;n của m&agrave;u gỗ.&nbsp;</p>\r\n\r\n<h2>Gạch men rạn</h2>\r\n\r\n<p>Gạch men rạn l&agrave; d&ograve;ng gạch trang tr&iacute; được l&agrave;m ho&agrave;n to&agrave;n bằng thủ c&ocirc;ng. Loại gạch n&agrave;y c&oacute; nguồn gốc từ Morocco v&agrave; ng&agrave;y c&agrave;ng được sử dụng rộng r&atilde;i cho c&aacute;c khu vực c&oacute; y&ecirc;u cầu cao về mặt thẩm mỹ như nh&agrave; h&agrave;ng, căn hộ cao cấp,... Gạch men rạn gi&uacute;p tạo n&ecirc;n vẻ đẹp thu h&uacute;t v&agrave; ấn tượng cho kh&ocirc;ng gian nội thất của gia đ&igrave;nh.&nbsp;</p>\r\n\r\n<p>Tr&ecirc;n đ&acirc;y l&agrave; th&ocirc;ng tin về một số loại gạch ốp l&aacute;t ph&ugrave; hợp khi thiết kế nội thất tạo n&ecirc;n vẻ đẹp sang trọng, cao cấp cho kh&ocirc;ng gian nội thất. Hy vọng những th&ocirc;ng tin n&agrave;y sẽ hữu &iacute;ch cho bạn.&nbsp;</p>\r\n\r\n<p>Li&ecirc;n hệ ngay để được tư vấn đầy đủ những c&ocirc;ng tr&igrave;nh nội thất nh&agrave; ở tuyệt nhất, hợp phong thủy d&agrave;nh cho bạn!</p>\r\n\r\n<p>C&Ocirc;NG TY CP ROYALHOME VIỆT NAM</p>\r\n\r\n<p>➡ Văn ph&ograve;ng: Số 20LK6D &ndash; L&agrave;ng Việt Kiều Ch&acirc;u &Acirc;u &ndash; Mỗ Lao &ndash; H&agrave; Đ&ocirc;ng &ndash; H&agrave; Nội</p>\r\n\r\n<p>➡ Website: http://www.thietkeroyalhome.com</p>\r\n\r\n<p>☎ Hotline: 0966655500</p>\r\n\r\n<p>✉ Email: thietkeroyalhome@gmail.com</p>\r\n', 'struc_img/tt8.jpg', '2017-12-05', 'Gạch ốp lát là một trong những yếu tố quan trọng tạo nên vẻ đẹp...');
INSERT INTO `tbl_news` (`News_ID`, `Cate_ID`, `News_Name`, `Description`, `Image`, `Time`, `Descrip_Demo`) VALUES
('TT_03', 16, 'Cách thiết kế ánh sáng hợp lý cho phòng ngủ', '<p><strong><em>Việc b&agrave;y tr&iacute; &aacute;nh s&aacute;ng cho ph&ograve;ng ngủ rất quan trọng. N&oacute; gi&uacute;p tăng th&ecirc;m vẻ đẹp thẩm mỹ cho căn ph&ograve;ng đồng thời đem lại cảm gi&aacute;c thoải m&aacute;i, dễ chịu cho bạn khi nghỉ ngơi.&nbsp;</em></strong></p>\r\n\r\n<h2>Nguy&ecirc;n tắc để thiết kế &aacute;nh s&aacute;ng cho ph&ograve;ng ngủ</h2>\r\n\r\n<p>Đầu ti&ecirc;n, bạn cần t&iacute;nh to&aacute;n mọi thứ thật cẩn thận trước khi b&agrave;y tr&iacute; &aacute;nh s&aacute;ng cho ph&ograve;ng ngủ. Bạn n&ecirc;n lựa chọn &aacute;nh s&aacute;ng mềm mại cho kh&ocirc;ng gian n&agrave;y đồng thời đ&aacute;p ứng những c&ocirc;ng năng cụ thể. Chẳng hạn như bố tr&iacute; &aacute;nh s&aacute;ng d&ugrave;ng cho b&agrave;n trang điểm, để đọc s&aacute;ch,...</p>\r\n\r\n<p><img style=\"width:100%;\" alt=\"\" src=\"https://thietkeroyalhome.com/images/projects/2019/06/27/large/279f3c34af4b4b15125a_1561609567.jpg\" style=\"height:600px; width:1200px\" /></p>\r\n\r\n<p>Ngo&agrave;i ra, bạn cũng cần lưu &yacute; việc l&ecirc;n sơ đồ điện cho ph&ograve;ng ngủ cũng như bố tr&iacute; vị tr&iacute; của c&ocirc;ng tắc sao cho thuận tiện nhất. Khi v&agrave;o ph&ograve;ng, bạn cần c&oacute; được &aacute;nh s&aacute;ng, do đ&oacute; h&atilde;y bố tr&iacute; một c&ocirc;ng tắc ngay ngo&agrave;i cửa. B&ecirc;n cạnh đ&oacute;, bạn c&oacute; thể sử dụng c&ocirc;ng tắc đảo chiều để dễ d&agrave;ng bật đ&egrave;n khi mở cửa, tắt đ&egrave;n ngay tr&ecirc;n giường ngủ một c&aacute;ch thuận tiện m&agrave; kh&ocirc;ng cần d&ugrave;ng nhiều b&oacute;ng đ&egrave;n.&nbsp;</p>\r\n\r\n<h2>Sử dụng &aacute;nh s&aacute;ng ph&ograve;ng ngủ theo chức năng</h2>\r\n\r\n<p>Việc bố tr&iacute; &aacute;nh s&aacute;ng cho ph&ograve;ng ngủ c&ograve;n t&ugrave;y thuộc v&agrave;o diện t&iacute;ch cũng như chức năng của &aacute;nh s&aacute;ng m&agrave; bạn muốn đem lại. Chẳng hạn như bạn muốn c&oacute; thể xem TV tr&ecirc;n giường c&ugrave;ng c&aacute;c con, một chiếc đ&egrave;n b&agrave;n ở đầu hoặc cạnh giường sẽ l&agrave; lựa chọn th&iacute;ch hợp. Ngược lại, nếu bạn mong muốn ph&ograve;ng ngủ l&agrave; một kh&ocirc;ng gian để nghỉ ngơi một c&aacute;ch thoải m&aacute;i nhất, b&oacute;ng đ&egrave;n n&ecirc;n được b&agrave;y tr&iacute; tại c&aacute;c khu vực trần nh&agrave;, hốc tường.</p>\r\n\r\n<p><img style=\"width:100%;\" alt=\"\" src=\"https://thietkeroyalhome.com/images/projects/2019/03/09/large/ngubome2_1552114869.jpg.jpg\" style=\"height:600px; width:1200px\" /></p>\r\n\r\n<h2>&Aacute;nh s&aacute;ng trang tr&iacute; cho ph&ograve;ng ngủ</h2>\r\n\r\n<p>B&ecirc;n cạnh việc thiết kế &aacute;nh s&aacute;ng ch&iacute;nh cho ph&ograve;ng ngủ, bạn cũng c&oacute; thể tận dụng ch&uacute;ng để l&agrave;m nổi bật vẻ đẹp thẩm mỹ cho kiến tr&uacute;c, tranh, tượng đặt trong ph&ograve;ng. Tuy nhi&ecirc;n, t&ugrave;y thuộc v&agrave;o mong muốn của bạn, &aacute;nh s&aacute;ng sẽ được xử l&yacute; cho ph&ugrave; hợp v&agrave; tạo sự h&agrave;i h&ograve;a nhất.&nbsp;&nbsp;</p>\r\n\r\n<p><img style=\"width:100%;\" alt=\"\" src=\"https://thietkeroyalhome.com/images/projects/2019/02/01/large/nhalotranphu19.jpg\" style=\"height:600px; width:1200px\" /></p>\r\n\r\n<p>Đối với những căn ph&ograve;ng ngủ rộng r&atilde;i, để kh&ocirc;ng c&oacute; sự trống trải, bạn c&oacute; thể bố tr&iacute; th&ecirc;m c&aacute;c bức tranh nghệ thuật treo tường. Kết hợp với đ&oacute; bạn n&ecirc;n sử dụng đ&egrave;n hắt s&aacute;ng tạo điểm nhấn nổi bật, nghệ thuật cho kh&ocirc;ng gian.&nbsp;</p>\r\n\r\n<h2>Lựa chọn m&agrave;u sắc &aacute;nh s&aacute;ng ph&ograve;ng ngủ</h2>\r\n\r\n<p>Đối với m&agrave;u sắc của &aacute;nh s&aacute;ng ph&ograve;ng ngủ, bạn n&ecirc;n lựa chọn những dải &aacute;nh s&aacute;ng từ m&agrave;u lạnh java đến m&agrave;u hồng nhạt. Ch&uacute;ng l&agrave; những m&agrave;u sắc nhẹ dịu đem đến cho ph&ograve;ng ngủ cảm gi&aacute;c thoải m&aacute;i, b&igrave;nh y&ecirc;n. Ngo&agrave;i ra, m&agrave;u sắc từ chụp đ&egrave;n, &aacute;nh s&aacute;ng b&oacute;ng đ&egrave;n cũng g&oacute;p phần tăng th&ecirc;m vẻ đẹp nổi bật, ấn tượng cho căn ph&ograve;ng.&nbsp;</p>\r\n\r\n<p><img style=\"width:100%;\" alt=\"\" src=\"https://thietkeroyalhome.com/images/projects/2019/03/19/large/penhouse-chihoan-phongngu5-1.jpg\" style=\"height:600px; width:1200px\" /></p>\r\n\r\n<h2>&Aacute;nh s&aacute;ng trong ph&ograve;ng ngủ của b&eacute;</h2>\r\n\r\n<p>Đối với trẻ nhỏ, việc thiết kế &aacute;nh s&aacute;ng rất quan trọng để b&eacute; cảm thấy thoải m&aacute;i, y&ecirc;u th&iacute;ch. Bạn n&ecirc;n bố tr&iacute; đ&egrave;n tại đầu giường cho trẻ, tuy nhi&ecirc;n để kh&ocirc;ng khiến b&eacute; bị ch&oacute;i mắt, kh&oacute; chịu, h&atilde;y thiết kế đ&egrave;n tại vị tr&iacute; cao. Trước khi ngủ, mẹ c&oacute; thể đọc s&aacute;ch cho b&eacute;, v&igrave; thế trang bị một chiếc đ&egrave;n b&agrave;n cạnh giường sẽ rất th&iacute;ch hợp.&nbsp;</p>\r\n\r\n<p>Đặc biệt, cần lưu &yacute; lựa chọn cường độ &aacute;nh s&aacute;ng của đ&egrave;n cho ph&ugrave; hợp. Bạn n&ecirc;n lựa chọn đ&egrave;n ngủ với &aacute;nh s&aacute;ng dịu nhẹ vừa phải gi&uacute;p trẻ c&oacute; giấc ngủ s&acirc;u hơn. Ngo&agrave;i ra, để tr&aacute;nh cho trẻ kh&ocirc;ng cẩn thận va, đụng v&agrave;o b&oacute;ng đ&egrave;n, bạn h&atilde;y sử dụng những loại đ&egrave;n c&oacute; gắn chụp gi&uacute;p đảm bảo an to&agrave;n.&nbsp;</p>\r\n\r\n<p>Việc thiết kế &aacute;nh s&aacute;ng cho ph&ograve;ng ngủ l&agrave; điều rất quan trọng g&oacute;p phần tạo n&ecirc;n vẻ đẹp v&agrave; sự thoải m&aacute;i, tiện nghi cho căn ph&ograve;ng. Tham khảo v&agrave; &aacute;p dụng c&aacute;ch thiết kế &aacute;nh s&aacute;ng cho ph&ograve;ng ngủ của gia đ&igrave;nh m&igrave;nh nh&eacute;!</p>\r\n\r\n<p>Li&ecirc;n hệ ngay để được tư vấn đầy đủ những c&ocirc;ng tr&igrave;nh nội thất nh&agrave; ở tuyệt nhất, hợp phong thủy d&agrave;nh cho bạn!</p>\r\n\r\n<p>C&Ocirc;NG TY CP ROYALHOME VIỆT NAM</p>\r\n\r\n<p>➡ Văn ph&ograve;ng: Số 20LK6D &ndash; L&agrave;ng Việt Kiều Ch&acirc;u &Acirc;u &ndash; Mỗ Lao &ndash; H&agrave; Đ&ocirc;ng &ndash; H&agrave; Nội</p>\r\n\r\n<p>➡ Website: http://www.thietkeroyalhome.com</p>\r\n\r\n<p>☎ Hotline: 0966655500</p>\r\n\r\n<p>✉ Email: thietkeroyalhome@gmail.com</p>\r\n', 'struc_img/tt2.jpg', '2019-12-19', 'Việc bày trí ánh sáng cho phòng ngủ rất quan trọng. Nó giúp tăng thêm...'),
('TT_04', 16, 'Cách treo rèm cửa hợp phong thủy, đem lại vượng khí cho ngôi nhà', '<p><strong><em>R&egrave;m cửa kh&ocirc;ng chỉ đơn giản l&agrave; sản phẩm để che mưa, chắn gi&oacute; cho ng&ocirc;i nh&agrave;. C&aacute;ch treo r&egrave;m cửa đ&uacute;ng phong thủy c&ograve;n đem đến vượng kh&iacute;, h&uacute;t t&agrave;i lộc v&agrave;o nh&agrave; cho gia chủ. C&ugrave;ng t&igrave;m hiểu một số th&ocirc;ng tin hữu &iacute;ch về c&aacute;ch treo r&egrave;m cửa&nbsp; trong b&agrave;i viết dưới đ&acirc;y.</em></strong>&nbsp;</p>\r\n\r\n<h2>C&aacute;ch chọn r&egrave;m theo hướng cửa</h2>\r\n\r\n<p>R&egrave;m c&oacute; li&ecirc;n quan rất mật thiết đến hệ thống cửa trong nh&agrave;, đem đến một kh&ocirc;ng gian h&agrave;i h&ograve;a. R&egrave;m cửa đồng thời cũng l&agrave; một vật liệu trang tr&iacute; gi&uacute;p thiết kế nội thất trở n&ecirc;n sang trọng, tinh tế hơn. Ngo&agrave;i ra, sử dụng r&egrave;m cửa ph&ugrave; hợp cũng g&oacute;p phần che lấp đi những khuyết điểm khi thiết kế ng&ocirc;i nh&agrave;.&nbsp;</p>\r\n\r\n<p><img style=\"width:100%;\" alt=\"\" src=\"https://thietkeroyalhome.com/images/projects/2019/03/19/original/penhouse-chihoan-phongkhach12-2_1552975431.jpg\" style=\"height:700px; width:1400px\" /></p>\r\n\r\n<p>Nếu ng&ocirc;i nh&agrave; c&oacute; hướng cửa kh&ocirc;ng tốt, bạn n&ecirc;n lựa chọn những loại r&egrave;m chất liệu d&agrave;y bằng gỗ, tre,... Ch&uacute;ng sẽ gi&uacute;p cho ng&ocirc;i nh&agrave; của bạn giảm đi s&aacute;t kh&iacute; đồng thời bảo vệ cửa lu&ocirc;n bền đẹp theo thời gian. Ngược lại, bạn c&oacute; thể dễ d&agrave;ng lựa chọn chất liệu, kiểu r&egrave;m theo &yacute; th&iacute;ch nếu ng&ocirc;i nh&agrave; c&oacute; hướng cửa tốt.&nbsp;</p>\r\n\r\n<h2>Lựa chọn r&egrave;m cửa theo từng kh&ocirc;ng gian</h2>\r\n\r\n<p>Tại bất kỳ kh&ocirc;ng gian nội thất n&agrave;o từ ph&ograve;ng kh&aacute;ch, ph&ograve;ng bếp, ph&ograve;ng ngủ,... bạn cần treo r&egrave;m cửa đảm bảo yếu tố thẩm mỹ cũng như ph&ugrave; hợp về mặt phong thủy. Trong đ&oacute;, ph&ograve;ng kh&aacute;ch l&agrave; nơi gia đ&igrave;nh sinh hoạt, diễn ra nhiều hoạt động chung. Bạn n&ecirc;n lựa chọn r&egrave;m cửa c&oacute; gam m&agrave;u s&aacute;ng, l&agrave;m tăng vận kh&iacute; cho kh&ocirc;ng gian.&nbsp;</p>\r\n\r\n<p><img style=\"width:100%;\" alt=\"\" src=\"https://thietkeroyalhome.com/images/projects/2019/02/01/large/tang1-pkhachbep3.jpg\" style=\"height:600px; width:1200px\" /></p>\r\n\r\n<p>C&ograve;n đối với ph&ograve;ng ngủ, những chất liệu r&egrave;m d&agrave;y dặn v&agrave; m&agrave;u sắc dịu nhẹ sẽ rất ph&ugrave; hợp, đem lại cho bạn cảm giản b&igrave;nh y&ecirc;n, dễ chịu. Cuối c&ugrave;ng, tại khu vực nấu nướng, bạn n&ecirc;n lựa chọn r&egrave;m cửa c&oacute; m&agrave;u sắc trung t&iacute;nh v&agrave; đơn giản.&nbsp;</p>\r\n\r\n<h2>Chọn lựa m&agrave;u sắc cho r&egrave;m cửa theo phong thủy</h2>\r\n\r\n<p>Khi treo r&egrave;m cửa, bạn cũng cần ch&uacute; &yacute; lựa chọn m&agrave;u sắc sao cho ph&ugrave; hợp với c&aacute;c hướng. Đối với cửa ở hướng Nam v&agrave; T&acirc;y Nam, bạn c&oacute; thể lựa chọn r&egrave;m cửa m&agrave;u đỏ. Tuy nhi&ecirc;n, n&ecirc;n tr&aacute;nh sử dụng những họa tiết v&agrave; h&igrave;nh khối cho r&egrave;m. C&ograve;n với hướng Đ&ocirc;ng v&agrave; Đ&ocirc;ng Nam, r&egrave;m m&agrave;u n&acirc;u hay xanh, c&oacute; kiểu d&aacute;ng nhẹ nh&agrave;ng sẽ ph&ugrave; hợp phong thủy.&nbsp;</p>\r\n\r\n<p><img style=\"width:100%;\" alt=\"\" src=\"https://thietkeroyalhome.com/images/projects/2019/02/01/large/chungcu-cincomcenter-ngumaster4.jpg\" style=\"height:600px; width:1200px\" /></p>\r\n\r\n<p>Nếu cửa hoặc cửa sổ trong nh&agrave; theo hướng T&acirc;y, T&acirc;y Bắc, bạn n&ecirc;n lựa chọn những r&egrave;m cửa c&oacute; m&agrave;u be, x&aacute;m hay trắng. Trong khi đ&oacute;, tại hướng Nam v&agrave; T&acirc;y Nam, c&oacute; thể sử dụng r&egrave;m cửa được trang tr&iacute; họa tiết b&egrave;o nh&uacute;n, thắt nơ sẽ đem lại nhiều vượng kh&iacute; cho ng&ocirc;i nh&agrave;. C&oacute;n với hướng Bắc, m&agrave;u sắc ph&ugrave; hợp khi lựa chọn r&egrave;m cửa l&agrave; m&agrave;u xanh. Để tăng th&ecirc;m kh&iacute; cho căn ph&ograve;ng, bạn c&oacute; thể lựa chọn kiểu d&aacute;ng r&egrave;m lượn s&oacute;ng, xếp ly,... sẽ rất th&iacute;ch hợp.&nbsp;</p>\r\n\r\n<h2>Chọn họa tiết cho r&egrave;m cửa hợp phong thủy</h2>\r\n\r\n<p>Bạn n&ecirc;n tr&aacute;nh lựa chọn những r&egrave;m cửa c&oacute; họa tiết h&igrave;nh th&uacute; dữ, b&uacute;p b&ecirc; ma,... Bởi n&oacute; l&agrave; biểu tượng của điềm gở, kh&ocirc;ng tốt cho ng&ocirc;i nh&agrave; đặc biệt l&agrave; khi c&oacute; người gi&agrave; v&agrave; trẻ nhỏ. Thay v&agrave;o đ&oacute;, bạn n&ecirc;n lựa chọn những họa tiết đơn giản, ph&ugrave; hợp với từng kh&ocirc;ng gian trong ph&ograve;ng.</p>\r\n\r\n<p><img style=\"width:100%;\" alt=\"\" src=\"https://thietkeroyalhome.com/images/projects/2019/03/19/original/01---khach-_1552979441.jpg\" style=\"height:600px; width:1200px\" /></p>\r\n\r\n<p>Treo r&egrave;m cửa hợp phong thủy l&agrave; việc l&agrave;m quan trọng gi&uacute;p tăng vượng kh&iacute;, đem lại may mắn cho gia đ&igrave;nh. Tham khảo v&agrave; &aacute;p dụng c&aacute;ch lựa chọn r&egrave;m cửa tr&ecirc;n để ph&ugrave; hợp với phong thủy của gia đ&igrave;nh m&igrave;nh nh&eacute;!</p>\r\n\r\n<p>Li&ecirc;n hệ ngay để được tư vấn đầy đủ những c&ocirc;ng tr&igrave;nh nội thất nh&agrave; ở tuyệt nhất, hợp phong thủy d&agrave;nh cho bạn!</p>\r\n\r\n<p>C&Ocirc;NG TY CP ROYALHOME VIỆT NAM</p>\r\n\r\n<p>➡ Văn ph&ograve;ng: Số 20LK6D &ndash; L&agrave;ng Việt Kiều Ch&acirc;u &Acirc;u &ndash; Mỗ Lao &ndash; H&agrave; Đ&ocirc;ng &ndash; H&agrave; Nội</p>\r\n\r\n<p>➡ Website: http://www.thietkeroyalhome.com</p>\r\n\r\n<p>☎ Hotline: 0966655500</p>\r\n\r\n<p>✉ Email: thietkeroyalhome@gmail.com</p>\r\n', 'struc_img/tt6.jpg', '2019-09-17', 'Rèm cửa không chỉ đơn giản là sản phẩm để che mưa, chắn gió cho...'),
('TT_05', 16, 'KHÔNG GIAN HOÀNG GIA CHÂU ÂU NHƯ THU NHỎ TRONG CĂN HỘ NHÀ BẠN', '<p><strong>Chắc hẳn khi xem một bộ phim cổ trang phương T&acirc;y hay đọc lịch sử, t&agrave;i liệu, theo d&otilde;i tr&ecirc;n ti vi, ch&uacute;ng ta cũng đ&ocirc;i lần ngưỡng mỗi vẻ nguy nga lộng lẫy của c&aacute;c cung điện đặc trưng nơi đ&acirc;y. Những cũng điện kiểu ho&agrave;ng gia Ch&acirc;u &Acirc;u lu&ocirc;n mang những phong c&aacute;ch kiến tr&uacute;c đặc trưng, thể hiện sự vương giả quyền qu&yacute;, cuộc sống xa hoa của những con người sống trong đ&oacute;. Sẽ thật tuyệt vời nếu như ta được sống trong ng&ocirc;i nh&agrave; như vậy.</strong></p>\r\n\r\n<p>Thời gian tr&ocirc;i đi, dưới t&aacute;c động của to&agrave;n cầu h&oacute;a, vẻ đẹp kiến tr&uacute;c ấy kh&ocirc;ng c&ograve;n bị giới hạn ở phương T&acirc;y m&agrave; c&ograve;n đem ra thế giới v&agrave; được ứng dụng phổ biến hơn, đặc biệt l&agrave; v&agrave;o c&aacute;c c&ocirc;ng tr&igrave;nh nh&agrave; ở.</p>\r\n\r\n<p>Phong c&aacute;ch kiến tr&uacute;c ấy được gọi l&agrave; t&acirc;n cổ điển bởi n&oacute; lược bớt những n&eacute;t rườm r&agrave; của phong c&aacute;ch kiến tr&uacute;c cổ điển v&agrave; thay v&agrave;o đ&oacute; l&agrave; những đường n&eacute;t đơn giản hơn, vẫn đảm bảo về mặt thẩm mỹ v&agrave; vẻ đẹp vốn c&oacute; của phong c&aacute;ch cổ điển, th&ecirc;m v&agrave;o đ&oacute; l&agrave; t&iacute;nh ứng dụng cao v&agrave; th&iacute;ch hợp với cuộc sống hiện đại.</p>\r\n\r\n<p>Phong c&aacute;ch t&acirc;n cổ điển l&agrave; phong c&aacute;ch thế mạnh trong thiết kế &amp; thi c&ocirc;ng nội thất của Royal Home. H&atilde;y c&ugrave;ng chi&ecirc;m ngưỡng căn hộ của gia đ&igrave;nh anh Long ở Vincom Center để thưởng thức vẻ đẹp ho&agrave;ng gia Ch&acirc;u &Acirc;u như thu nhỏ trong căn nh&agrave; nh&eacute;!</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img style=\"width:100%;\" alt=\"\" src=\"https://thietkeroyalhome.com/upload_images/images/2019/09/27/chungcu-vincomcenter-pk1_1552958626.jpg\" style=\"height:700px; width:1400px\" /></p>\r\n\r\n<p>Bao ch&ugrave;m to&agrave;n bộ nội thất căn hộ l&agrave; m&agrave;u v&agrave;ng &aacute;nh kim kết hợp với nền tường trắng l&agrave;m bừng s&aacute;ng l&ecirc;n kh&ocirc;ng gian cả ng&ocirc;i nh&agrave;. Những đường ph&agrave;o, chỉ c&ugrave;ng với những bức tranh mang đậm nghệ thuật T&acirc;y phương thực sự khiến người ra li&ecirc;n tưởng đến một kh&ocirc;ng gian bảo t&agrave;ng nghệ thuật văn h&oacute;a của Ch&acirc;u &Acirc;u.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img style=\"width:100%;\" alt=\"\" src=\"https://thietkeroyalhome.com/upload_images/images/2019/09/27/chungcu-vincomcenter-pk2.jpg\" style=\"height:600px; width:1200px\" /></p>\r\n\r\n<p><img style=\"width:100%;\" alt=\"\" src=\"https://thietkeroyalhome.com/upload_images/images/2019/09/27/chungcu-vincomcenter-pk3.jpg\" style=\"height:600px; width:1200px\" /></p>\r\n\r\n<p>Kh&ocirc;ng gian bếp ăn rất th&iacute;ch hợp để b&agrave;y biện những bữa ăn sang trọng, với rượu vang, với đ&egrave;n, nến v&agrave; hoa. &Aacute;nh đ&egrave;n ch&ugrave;m tỏa xuống thật sự chẳng kh&aacute;c n&agrave;o một bữa tối sang trọng tại nh&agrave; h&agrave;ng, trong một kh&ocirc;ng gian ri&ecirc;ng tư d&agrave;nh cho những người quan trọng.</p>\r\n\r\n<p>Trong khu&ocirc;n khổ một gia đ&igrave;nh, bếp ăn kiểu n&agrave;y cũng ho&agrave;n to&agrave;n ph&ugrave; hợp với những bữa ăn ấm c&uacute;ng tụ họp gia đ&igrave;nh.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img style=\"width:100%;\" alt=\"\" src=\"https://thietkeroyalhome.com/upload_images/images/2019/09/27/chungcu-vincomcenter-pk5.jpg\" style=\"height:600px; width:1200px\" /></p>\r\n\r\n<p><img style=\"width:100%;\" alt=\"\" src=\"https://thietkeroyalhome.com/upload_images/images/2019/09/27/chungcu-vincomcenter-pk4.jpg\" style=\"height:600px; width:1200px\" /></p>\r\n\r\n<p>Thực sự một lối sống kiểu Ch&acirc;u &Acirc;u được thu nhỏ trong ng&ocirc;i nh&agrave; của anh Long. C&aacute;c KTS đ&atilde; khai th&aacute;c những yếu tố văn h&oacute;a, lịch sử của T&acirc;y Phương để mang v&agrave;o kh&ocirc;ng gian ng&ocirc;i nh&agrave;. Từng họa tiết tr&ecirc;n c&aacute;c bức tranh, đến chiếc đồng hồ nhỏ để đặt trưng b&agrave;y hay chiếc piano đặt trang trọng ở một g&oacute;c đều l&agrave;m t&ocirc;n vinh những n&eacute;t đẹp tuy nhỏ nhưng rất &quot;t&acirc;n cổ điển&quot;.</p>\r\n\r\n<p>Nội thất t&acirc;n cổ điển ưa chuộng những h&igrave;nh vẽ cầu kỳ bắt mắt, tạo n&ecirc;n sự mềm mại, uyển chuyển.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img style=\"width:100%;\" alt=\"\" src=\"https://thietkeroyalhome.com/upload_images/images/2019/09/27/chungcu-cincomcenter-ngumaster4.jpg\" style=\"height:600px; width:1200px\" /></p>\r\n\r\n<p><img style=\"width:100%;\" alt=\"\" src=\"https://thietkeroyalhome.com/upload_images/images/2019/09/27/chungcu-cincomcenter-ngumaster2.jpg\" style=\"height:600px; width:1200px\" /></p>\r\n\r\n<p>Ph&ograve;ng ngủ cũng vậy, phong c&aacute;ch rất liền mạch với ph&ograve;ng kh&aacute;ch bếp. Vẫn l&agrave; m&agrave;u trắng v&agrave; v&agrave;ng bạch kim sang trong nhưng ph&ograve;ng ngủ cho ra cảm gi&aacute;c b&igrave;nh y&ecirc;n hơn.</p>\r\n\r\n<p>Kh&ocirc;ng gian l&agrave;m việc ri&ecirc;ng biệt, y&ecirc;n tĩnh, c&aacute;ch xa chỗ ngủ. Từng họa tiết uốn lượn tr&ecirc;n cột tường, tr&ecirc;n c&aacute;c đồ nội thất thể hiện sự tỷ mỷ, chăm ch&uacute;t v&agrave; khẳng định, n&acirc;ng cao gi&aacute; trị của chủ căn ph&ograve;ng.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Như vậy, cả kh&ocirc;ng gian Ch&acirc;u &Acirc;u như thu nhỏ trong căn nh&agrave; của anh Long bằng nghệ thuật kiến tr&uacute;c t&acirc;n cổ điển v&agrave; b&agrave;n tay, &oacute;c s&aacute;ng tạo của c&aacute;c KTS.</p>\r\n\r\n<p>Nếu y&ecirc;u th&iacute;ch v&agrave; muốn sở hữu kh&ocirc;ng gian sống t&acirc;n cổ điển như tr&ecirc;n, đừng ngần ngại li&ecirc;n hệ ngay với Royal Home để được kiến tr&uacute;c sư tr&ecirc;n 9 năm kinh nghiệm của ch&uacute;ng t&ocirc;i tư vấn nh&eacute;!</p>\r\n\r\n<p>----------------------</p>\r\n\r\n<p>ROYAL HOME :</p>\r\n\r\n<p>▸ Tư vấn &amp; thiết kế, thi c&ocirc;ng nội thất TRỌN G&Oacute;I</p>\r\n\r\n<p>▸ L&yacute; tưởng cho NH&Agrave; Ở, CĂN HỘ, BIỆT THỰ CAO CẤP</p>\r\n\r\n<p>▸ Quy tr&igrave;nh r&otilde; r&agrave;ng, đảm bảo thi c&ocirc;ng Đ&Uacute;NG TIẾN ĐỘ</p>\r\n\r\n<p>▸ Ch&iacute;nh s&aacute;ch bảo h&agrave;nh c&ocirc;ng tr&igrave;nh L&Ecirc;N ĐẾN 7 NĂM</p>\r\n\r\n<p>▸ Đội ngũ Kiến tr&uacute;c sư tr&ecirc;n 9 NĂM KINH NGHIỆM</p>\r\n\r\n<p>-----------------------</p>\r\n\r\n<p>C&Ocirc;NG TY CP ROYALHOME VIỆT NAM</p>\r\n\r\n<p>➡ Văn ph&ograve;ng: Số 20LK6D &ndash; L&agrave;ng Việt Kiều Ch&acirc;u &Acirc;u &ndash; Mỗ Lao &ndash; H&agrave; Đ&ocirc;ng &ndash; H&agrave; Nội</p>\r\n\r\n<p>➡ Website: http://www.thietkeroyalhome.com</p>\r\n\r\n<p>☎ Hotline: 0966655500 ✉ Email: thietkeroyalhome@gmail.com</p>\r\n', 'struc_img/tt5.jpg', '2019-12-24', 'Chắc hẳn khi xem một bộ phim cổ trang phương Tây hay đọc lịch sử,..'),
('TT_06', 16, 'GIÚP BẠN TIẾT KIỆM ĐẾN 40% CHI PHÍ NỘI THẤT NHỜ GIẢI PHÁP THIẾT KẾ & THI CÔNG NỘI THẤT TRỌN GÓI', '<p><strong>Khi ho&agrave;n thiện một ng&ocirc;i nh&agrave;, sẽ rất vất vả cho gia chủ khi phải mua sắm từng đồ nội thất sao cho vừa ph&ugrave; hợp với k&iacute;ch thước của từng ph&ograve;ng, từng kh&ocirc;ng gian. Kh&ocirc;ng những vậy, gia chủ c&ograve;n phải tự lắp đặt c&aacute;c hệ thống điện, nước... v&agrave; c&aacute;c dịch vụ li&ecirc;n quan đến lắp đặt nội thất kh&aacute;c. Hiểu được nỗi khổ đ&oacute;, dịch vụ thiết kế &amp; thi c&ocirc;ng nội thất ra đời. N&oacute; chẳng những gi&uacute;p đơn giản h&oacute;a qu&aacute; tr&igrave;nh ho&agrave;n thiện nội thất m&agrave; c&ograve;n gi&uacute;p tiết kiệm đến 40% chi ph&iacute;.</strong></p>\r\n\r\n<p>C&ugrave;ng Royal Home kh&aacute;m ph&aacute; về lợi &iacute;ch dịch vụ thiết kế &amp; thi c&ocirc;ng nội thất trọn g&oacute;i ngay dưới đ&acirc;y nh&eacute;!</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Tối ưu về thời gian thi c&ocirc;ng v&agrave; chi ph&iacute; thi c&ocirc;ng, lắp đặt</strong></p>\r\n\r\n<p>Chắc hẳn tiết kiệm v&agrave; tối ưu chi ph&iacute; &amp; thời gian l&agrave; điều ai cũng mong muốn khi t&igrave;m kiếm một giải ph&aacute;p nội thất, v&agrave; dịch vụ thiết kế &amp; thi c&ocirc;ng nội thất đ&atilde; l&agrave;m được điều đ&oacute;. Nếu như bạn phải &quot;chạy đ&ocirc;n chạy đ&aacute;o&quot; để t&igrave;m v&agrave; li&ecirc;n hệ với rất nhiều b&ecirc;n như điện nước, dọn vệ sinh, cung cấp v&agrave; lắp đặt nội thất..., tương đương với mỗi dịch vụ đ&oacute; l&agrave; một khoản chi ph&iacute; kh&aacute;c nhau th&igrave; với dịch vụ thiết kế v&agrave; thi c&ocirc;ng nội thất trọn g&oacute;i, mọi thứ bạn chỉ cần l&agrave;m một lần. Mọi thứ bạn cần l&agrave;m l&agrave; đặt b&uacute;t k&yacute; hợp đồng (tất nhi&ecirc;n với điều kiện l&agrave; sau đ&atilde; h&agrave;i l&ograve;ng v&agrave; đạt được thoải thuận của cả hai b&ecirc;n) v&agrave; mọi chuyện c&ograve;n lại sẽ do b&ecirc;n c&ocirc;ng ty nội thất lo. Thật tiện lợi phải kh&ocirc;ng n&agrave;o?</p>\r\n\r\n<p><img style=\"width:100%;\" alt=\"\" src=\"https://thietkeroyalhome.com/upload_images/images/2019/09/19/k4_1553733043.jpg\" style=\"height:800px; width:1600px\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>C&oacute; được kh&ocirc;ng gian ph&ugrave; hợp cho ng&ocirc;i nh&agrave; của m&igrave;nh v&agrave; ph&ograve;ng ngừa rủi ro</strong></p>\r\n\r\n<p>Nếu như khi đi mua nội thất c&oacute; sẵn, bạn phải chọn lựa, căn đo thật kỹ lưỡng sao cho bộ sofa phải vừa với ph&ograve;ng kh&aacute;ch nh&agrave; m&igrave;nh, sao cho chiếc giường phải đặt vừa đ&uacute;ng vị tr&iacute; m&eacute;p cửa sổ hay sao cho chiếc tủ tường kh&ocirc;ng bị qu&aacute; lớn sao với bức tường ph&iacute;a sau... th&igrave; đương nhi&ecirc;n khi được thiết kế, mọi thứ sẽ trở n&ecirc;n thật vừa vặn đến từng g&oacute;c cạnh. Bạn c&oacute; thể thoải m&aacute;i tham khảo tr&ecirc;n bản thiết kế mặt bằng của Kiến tr&uacute;c sư, c&oacute; thể thay đổi vị tr&iacute; theo &yacute; muốn m&agrave; kh&ocirc;ng sợ lệch một ph&acirc;n n&agrave;o. Như vậy, sẽ kh&ocirc;ng c&ograve;n t&igrave;nh trạng m&agrave; thứ bạn th&iacute;ch th&igrave; kh&ocirc;ng vừa nh&agrave; bạn hay khi mua về lại bị thừa một v&agrave;i ph&acirc;n, tr&aacute;nh t&igrave;nh trạng đổi trả h&agrave;ng h&oacute;a v&agrave; hao m&ograve;n về chi ph&iacute;.</p>\r\n\r\n<p><img style=\"width:100%;\" alt=\"\" src=\"https://thietkeroyalhome.com/upload_images/images/2019/09/19/Khac%2Bsanh%2Bbep%20(2).jpg\" style=\"height:800px; width:1600px\" /></p>\r\n\r\n<p>Hơn thế nữa, khi k&yacute; kết hợp đồng với một đơn vị thiết kế v&agrave; thi c&ocirc;ng nội thất trọn g&oacute;i, họ sẽ c&oacute; những điều khoản đảm bảo về bảo h&agrave;nh, bảo tr&igrave; tư dưỡng sản phẩm để gia chủ c&oacute; thể y&ecirc;n t&acirc;m về chất lượng v&agrave; kh&ocirc;ng sợ chi ph&iacute; ph&aacute;t sinh</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Đảm bảo sự liền mạch vể thẩm mỹ</strong></p>\r\n\r\n<p>Với dịch vụ thiết kế v&agrave; thi c&ocirc;ng nội thất trọn g&oacute;i, kh&aacute;ch h&agrave;ng c&oacute; thể tự do lựa chọn phong c&aacute;ch y&ecirc;u th&iacute;ch của m&igrave;nh. Từ đ&oacute; c&aacute;c kiến tr&uacute;c sư sẽ dựa v&agrave;o đ&oacute; để thiết n&ecirc;n c&aacute;c kh&ocirc;ng gian nội thất đồng bộ từ ph&ograve;ng kh&aacute;ch, ph&ograve;ng bếp cho tới ph&ograve;ng ngủ. Điều đ&oacute; chẳng những l&agrave;m kh&ocirc;ng gian thẩm mỹ hơn, tr&aacute;nh sự &quot;l&ocirc;m c&ocirc;m&quot;, lộn xộn trong sắp xếp, b&agrave;i tr&iacute;. Bởi v&igrave;, c&aacute;c KTS sẽ biết c&aacute;ch sắp xếp như đo li đ&oacute;ng nếp cho từng g&oacute;c nh&agrave; bạn, biết t&acirc;n dụng v&agrave; điều khiển kh&ocirc;ng gian kh&ocirc;ng chỉ tiện dụng về c&ocirc;ng năng, hợp phong thủy m&agrave; c&ograve;n bắt mắt, c&oacute; m&agrave;u sắc đồng bộ, c&oacute; điểm nhấn r&otilde; r&agrave;ng.</p>\r\n\r\n<p><img style=\"width:100%;\" alt=\"\" src=\"https://thietkeroyalhome.com/upload_images/images/2019/09/19/tang-2---pngucon--shc--2_1552120972.jpg.jpg\" style=\"height:600px; width:1200px\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Tăng trải nghiệm sống, tốt cho tinh thần v&agrave; sức khỏe</strong></p>\r\n\r\n<p>Một kh&ocirc;ng gian thẩm mỹ, tiện lợi hẳn sẽ mang đến những trải nghiệm sống tuyệt vời. Đặc biệt hơn nữa khi kh&ocirc;ng gian ấy được tạo n&ecirc;n bởi &yacute; tưởng v&agrave; niềm y&ecirc;u th&iacute;ch của m&igrave;nh v&agrave; d&agrave;nh ri&ecirc;ng cho ng&ocirc;i nh&agrave; của m&igrave;nh.</p>\r\n\r\n<p><img style=\"width:100%;\" alt=\"\" src=\"https://thietkeroyalhome.com/upload_images/images/2019/09/19/13-bep%20va%20an_View05.jpg\" style=\"height:800px; width:1600px\" /></p>\r\n\r\n<p>Kh&ocirc;ng gian sống thoải m&aacute;i, như &yacute; muốn sẽ mang đến một tinh thần sảng kho&aacute;i. Hơn nữa n&oacute; được thiết kế một c&aacute;ch khoa học dưới b&agrave;n tay v&agrave; con mắt nghệ thuật của kiến tr&uacute;c sư sẽ tạo điều kiện tốt cho sức khỏe&nbsp;thể chất v&agrave; tinh thần cho gia đ&igrave;nh của bạn.</p>\r\n\r\n<p>H&atilde;y c&acirc;n nhắc để tối ưu chi ph&iacute; cũng như trải nghiệm sống cho gia đ&igrave;nh m&igrave;nh bạn nh&eacute;!</p>\r\n\r\n<p>-----------------------&nbsp;</p>\r\n\r\n<p>ROYAL HOME :</p>\r\n\r\n<p>▸ Tư vấn &amp; thiết kế, thi c&ocirc;ng nội thất TRỌN G&Oacute;I</p>\r\n\r\n<p>▸ L&yacute; tưởng cho NH&Agrave; Ở, CĂN HỘ, BIỆT THỰ CAO CẤP</p>\r\n\r\n<p>▸ Quy tr&igrave;nh r&otilde; r&agrave;ng, đảm bảo thi c&ocirc;ng Đ&Uacute;NG TIẾN ĐỘ</p>\r\n\r\n<p>▸ Ch&iacute;nh s&aacute;ch bảo h&agrave;nh c&ocirc;ng tr&igrave;nh L&Ecirc;N ĐẾN 7 NĂM</p>\r\n\r\n<p>▸ Đội ngũ Kiến tr&uacute;c sư tr&ecirc;n 9 NĂM KINH NGHIỆM</p>\r\n\r\n<p>--------------------------</p>\r\n\r\n<p>C&Ocirc;NG TY CP ROYALHOME VIỆT NAM</p>\r\n\r\n<p>➡ Văn ph&ograve;ng: Số 20LK6D &ndash; L&agrave;ng Việt Kiều Ch&acirc;u &Acirc;u &ndash; Mỗ Lao &ndash; H&agrave; Đ&ocirc;ng &ndash; H&agrave; Nội</p>\r\n\r\n<p>➡ Website: http://www.thietkeroyalhome.com</p>\r\n\r\n<p>☎ Hotline: 0966655500 ✉ Email: thietkeroyalhome@gmail.com</p>\r\n', 'struc_img/tt11.jpg', '2019-10-19', 'Khi hoàn thiện một ngôi nhà, sẽ rất vất vả cho gia chủ khi phải...'),
('TT_07', 16, 'NỘI THẤT PHONG CÁCH HIỆN ĐẠI MANG DẤU ẤN VÀ PHONG CÁCH CÁ NHÂN CỦA GIA CHỦ', '<p><strong>Bất cứ gia chủ n&agrave;o khi x&acirc;y dựng n&ecirc;n một kh&ocirc;ng gian sống cho m&igrave;nh th&igrave; cũng muốn kh&ocirc;ng gian sống đ&oacute; mang đậm dấu ấn c&aacute; nh&acirc;n, của gia đ&igrave;nh. Điều đ&oacute; như một sự khẳng định rằng kh&ocirc;ng gian ấy l&agrave; duy nhất. l&agrave; đặc biệt nhất v&agrave; c&oacute; &yacute; nghĩa với từng th&agrave;nh vi&ecirc;n của gia đ&igrave;nh</strong></p>\r\n\r\n<p><strong>Ch&iacute;nh v&igrave; vậy, một trong những yếu tố ch&iacute;nh tạo n&ecirc;n một kh&ocirc;ng gian hạnh ph&uacute;c ngo&agrave;i thẩm mỹ, c&ocirc;ng năng, phong thủy th&igrave; c&ograve;n l&agrave; phong c&aacute;ch v&agrave; dấu ấn c&aacute; nh&acirc;n. Đ&oacute; l&agrave; sự nỗ lực hợp t&aacute;c giữa kiến tr&uacute;c sư với gia chủ. Kiến tr&uacute;c sư c&oacute; nhiệm vụ lắng nghe v&agrave; truyền tải những &yacute; tưởng của gia chủ qua kh&ocirc;ng gian nội thất.</strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>C&ugrave;ng chi&ecirc;m ngưỡng kh&ocirc;ng gian chung cư của gia đ&igrave;nh anh Phong để xem c&aacute;c KTS Royal Home đ&atilde; tạo n&ecirc;n kh&ocirc;ng gian sống mang dấu ấn gia chủ như thế n&agrave;o nh&eacute;!</p>\r\n\r\n<p>Anh Phong rất th&iacute;ch phong c&aacute;ch hiện đại sang trọng, lịch sự v&agrave; hơi tối giản về m&agrave;u sắc. V&igrave; vậy, m&agrave;u trắng đục v&agrave; m&agrave;u x&aacute;m đậm được lựa chọn l&agrave;m m&agrave;u chủ đạo cho nội thất căn hộ. Đ&oacute;ng vai tr&ograve; l&agrave;m điểm nhấn cho ng&ocirc;i nh&agrave;, một số đồ nội thất phụ nổi bật hẳn l&ecirc;n trong kh&ocirc;ng gian với m&agrave;u n&acirc;u v&agrave;ng nhạt.</p>\r\n\r\n<p><img style=\"width:100%;\" alt=\"\" src=\"https://thietkeroyalhome.com/upload_images/images/2019/09/09/1.jpg\" style=\"height:750px; width:1500px\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Sự s&aacute;ng tạo độc đ&aacute;o của kh&ocirc;ng gian nội thất c&ograve;n được thể hiện ở bức họa v&ocirc; c&ugrave;ng độc đ&aacute;p ở bức tường ph&iacute;a sau salon. N&oacute; l&agrave;m cho kh&ocirc;ng gian nội thất trở n&ecirc;n độc đ&aacute;o, cuốn h&uacute;t nhờ vẻ c&aacute; t&iacute;nh của nghệ thuật hiện đại</p>\r\n\r\n<p><img style=\"width:100%;\" alt=\"\" src=\"https://thietkeroyalhome.com/upload_images/images/2019/09/09/9.jpg\" style=\"height:750px; width:1500px\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Khu vực nấu ăn kh&aacute; đơn giản, nối liền với ph&ograve;ng kh&aacute;ch.</p>\r\n\r\n<p>M&agrave;u sắc vẫn l&agrave; bao quang khổ m&agrave;u trắng, đen, x&aacute;m tại n&ecirc;n một kh&ocirc;ng gian liềm mạch. Thực sự, nếu nh&igrave;n qua, thật kh&oacute; để ph&acirc;n biệt được khu vực bếp t&aacute;ch biệt bởi sự sắp xếp, phối m&agrave;u những chiếc tủ bếp, m&aacute;y h&uacute;t kh&oacute;i, kệ nấu ăn hay từng đồ vật trang tr&iacute; tr&ecirc;n b&agrave;n ăn, tr&ecirc;n tủ tường đều rất h&agrave;i h&ograve;a với kh&ocirc;ng gian chung của căn hộ.</p>\r\n\r\n<p><img style=\"width:100%;\" alt=\"\" src=\"https://thietkeroyalhome.com/upload_images/images/2019/09/09/5.jpg\" style=\"height:750px; width:1500px\" /></p>\r\n\r\n<p><img style=\"width:100%;\" alt=\"\" src=\"https://thietkeroyalhome.com/upload_images/images/2019/09/09/8.jpg\" style=\"height:750px; width:1500px\" /></p>\r\n\r\n<p>Đ&acirc;y cũng l&agrave; một nghệ thuật sắp xếp v&agrave; phối m&agrave;u l&yacute; tưởng d&agrave;nh cho những căn hộ c&oacute; diện t&iacute;ch vừa, tạo cảm gi&aacute;c rộng r&atilde;i, thẩm mỹ hơn.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Một điều dễ thấy nhất của dấu ấn c&aacute; nh&acirc;n của gia đ&igrave;nh anh Phong ch&iacute;nh l&agrave; những bức ảnh gia đ&igrave;nh xuất hiện xuy&ecirc;n suốt kh&ocirc;ng gian ng&ocirc;i nh&agrave;, từ ph&ograve;ng ngủ cho đến ph&ograve;ng kh&aacute;ch bếp. Ch&uacute;ng được chuyển th&agrave;nh m&agrave;u đen trắng, vừa h&ograve;a hợp với kh&ocirc;ng gian ng&ocirc;i nh&agrave; lại tạo một cảm gi&aacute;c ho&agrave;i niệm ấm c&uacute;ng. Mỗi nơi mỗi g&oacute;c đều chan chứa kỉ niệm của gia đ&igrave;nh, mỗi bức ảnh l&agrave; mỗi dấu mốc quan trọng nhắc nhở c&aacute;c th&agrave;nh vi&ecirc;n lu&ocirc;n y&ecirc;u thương lẫn nhau.</p>\r\n\r\n<p><img style=\"width:100%;\" alt=\"\" src=\"https://thietkeroyalhome.com/upload_images/images/2019/09/09/1-ng.jpg\" style=\"height:883px; width:1500px\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Kh&ocirc;ng gian ph&ograve;ng ngủ với thiết kế ph&ograve;ng tắm v&ocirc; c&ugrave;ng hiện đại bằng k&iacute;nh trong suốt. Ta thường gặp những thiết kế n&agrave;y trong những kh&aacute;ch sạn hạng sang. Ph&ograve;ng ngủ kh&aacute; khi&ecirc;m tốn v&igrave; thế đ&acirc;y cũng l&agrave; một giải ph&aacute;p t&iacute;ch cực nhằm l&agrave;m cho kh&ocirc;ng gian ph&ograve;ng ngủ trở n&ecirc;n rộng hơn. Thử tưởng tượng nếu ph&ograve;ng tắm m&agrave; được thiết kế kh&eacute;p k&iacute;n như những ph&ograve;ng tắm thường thấy th&igrave; kh&ocirc;ng gian ph&ograve;ng ngủ sẽ trở n&ecirc;n b&iacute; b&aacute;ch đi rất nhiều. V&igrave; vậy thiết kế kiểu n&agrave;y kh&ocirc;ng những mang lại thẩm mỹ m&agrave; c&ograve;n khiến t&igrave;nh cảm vợ chồng thăng hoa hơn bao giờ hết</p>\r\n\r\n<p><img style=\"width:100%;\" alt=\"\" src=\"https://thietkeroyalhome.com/upload_images/images/2019/09/09/3-ng.jpg\" style=\"height:883px; width:1500px\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>D&ugrave; c&oacute; trang ho&agrave;ng kh&ocirc;ng gian đến đ&acirc;u th&igrave; vợ chồng anh Phong vẫn kh&ocirc;ng qu&ecirc;n d&agrave;nh một kh&ocirc;ng gian nhỏ học tập cho c&aacute;c con. Đ&uacute;ng với t&iacute;nh chất của trẻ nhỏ, kh&ocirc;ng gian kh&aacute; rực rỡ sắc m&agrave;u, để c&aacute;c con tha hồ s&aacute;ng tạo v&agrave; học tập.</p>\r\n\r\n<p><img style=\"width:100%;\" alt=\"\" src=\"https://thietkeroyalhome.com/upload_images/images/2019/09/09/10.jpg\" style=\"height:751px; width:1500px\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Như vậy, qua kh&ocirc;ng gian căn hộ của gia đ&igrave;nh anh Phong, ch&uacute;ng ta c&oacute; thể thấy mốt sự hiện đại, ph&oacute;ng kho&aacute;ng v&agrave; rất coi trọng t&igrave;nh cảm gia đ&igrave;nh của gia chủ. Đ&uacute;ng tinh thần của lối sống hiện đại, mọi kh&ocirc;ng gian trong nh&agrave; năm tr&ecirc;n một diện t&iacute;ch mặt bằng 80m2, kh&aacute; khi&ecirc;m tốn nhưng đầy đủ c&ocirc;ng năng.</p>\r\n\r\n<p>Từ c&aacute;ch sắp xếp đến m&agrave;u sắc đều v&ocirc; c&ugrave;ng nhất qu&aacute;n khiến cho căn hộ trở th&agrave;nh một kh&ocirc;ng gian l&yacute; tưởng của vợ chồng trẻ.</p>\r\n\r\n<p>H&atilde;y tham khảo cho kh&ocirc;ng gian căn hộ nh&agrave; m&igrave;nh bạn nh&eacute;!</p>\r\n\r\n<p>---------------------</p>\r\n\r\n<p>ROYAL HOME :</p>\r\n\r\n<p>▸ Tư vấn &amp; thiết kế, thi c&ocirc;ng nội thất TRỌN G&Oacute;I</p>\r\n\r\n<p>▸ L&yacute; tưởng cho NH&Agrave; Ở, CĂN HỘ, BIỆT THỰ CAO CẤP</p>\r\n\r\n<p>▸ Quy tr&igrave;nh r&otilde; r&agrave;ng, đảm bảo thi c&ocirc;ng Đ&Uacute;NG TIẾN ĐỘ</p>\r\n\r\n<p>▸ Ch&iacute;nh s&aacute;ch bảo h&agrave;nh c&ocirc;ng tr&igrave;nh L&Ecirc;N ĐẾN 7 NĂM</p>\r\n\r\n<p>▸ Đội ngũ Kiến tr&uacute;c sư tr&ecirc;n 9 NĂM KINH NGHIỆM</p>\r\n\r\n<p>---------------------</p>\r\n\r\n<p>C&Ocirc;NG TY CP ROYALHOME VIỆT NAM</p>\r\n\r\n<p>➡ Văn ph&ograve;ng: Số 20LK6D &ndash; L&agrave;ng Việt Kiều Ch&acirc;u &Acirc;u &ndash; Mỗ Lao &ndash; H&agrave; Đ&ocirc;ng &ndash; H&agrave; Nội</p>\r\n\r\n<p>➡ Website: http://www.thietkeroyalhome.com</p>\r\n\r\n<p>☎ Hotline: 0966655500</p>\r\n\r\n<p>✉ Email: thietkeroyalhome@gmail.com</p>\r\n', 'struc_img/tt7.jpg', '2019-10-21', 'Bất cứ gia chủ nào khi xây dựng nên một không gian sống cho mình...'),
('TT_08', 16, 'BIỆT THỰ PHONG CÁCH Á ĐÔNG KẾT HỢP HIỆN ĐẠI', '<p><strong>Sống trong một thế giới hiện đại lu&ocirc;n ph&aacute;t triển c&ugrave;ng với sự đa dạng về phong c&aacute;ch nội thất, ch&uacute;ng ta hẳn sẽ từng bắt gặp rất nhiều kh&ocirc;ng gian nội thất kh&aacute;c nhau. Cũng ch&iacute;nh bởi sự đa dạng trong phong c&aacute;ch nội thất v&agrave; sự tự do trong thiết kế theo &yacute; muốn v&agrave; sở th&iacute;ch, c&aacute;c kh&ocirc;ng gian kh&ocirc;ng nhất thiết phải chỉ sử dụng một phong c&aacute;ch nội thất. Tr&ecirc;n thực tế, sự giao thoa giữa c&aacute;c phong c&aacute;ch nội thất cũng rất phổ biến, để ph&ugrave; hợp về c&ocirc;ng năng cũng như tạo cảm gi&aacute;c mới mẻ cho kh&ocirc;ng gian sống.</strong></p>\r\n\r\n<p>Phong c&aacute;ch nội thất &Aacute; Đ&ocirc;ng kết hợp với phong c&aacute;ch nội thất hiện đại l&agrave; một sự giao thoa thường thấy trong c&aacute;c ng&ocirc;i nh&agrave; tại Việt Nam. Bởi Việt Nam cũng nằm trong nền văn h&oacute;a &Aacute; Đ&ocirc;ng, cộng với việc du nhập rất nhiều c&aacute;c yếu tố văn h&oacute;a phương T&acirc;y c&ugrave;ng với sự ph&aacute;t triển của cuộc sống hiện đại l&agrave;m cho c&aacute;c yếu tố được h&ograve;a trộn với nhau l&agrave;m n&ecirc;n những kh&ocirc;ng gian sống độc đ&aacute;o.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Biệt thự Ecopark của gia đ&igrave;nh anh Chinh d&oacute; c&aacute;c KTS Royal Home thiết kế l&agrave; một c&ocirc;ng tr&igrave;nh điển h&igrave;nh của sự kết hợp giữa phong c&aacute;c &aacute; đ&ocirc;ng v&agrave; hiện đại n&agrave;y. C&ugrave;ng chi&ecirc;m ngưỡng ngay dưới đ&acirc;y nh&eacute;!</p>\r\n\r\n<p>Ngay từ ở ph&ograve;ng kh&aacute;ch, ta đ&atilde; bắt gặp bộ sofa với sự kết hợp của phần tay vịn đặc trưng kiểu &Aacute; Đ&ocirc;ng c&ugrave;ng với phần nệm &ecirc;m &aacute;i ph&iacute;a dưới gối tựa ph&iacute;a sau mang m&agrave;u sắc kh&aacute; hiện đại. Sự kết hợp đ&oacute; chẳng những mang đến một d&aacute;ng h&igrave;nh rất độc đ&aacute;o, đẹp mắt cho bộ sofa m&agrave; c&ograve;n khiến người sử dụng cảm thất thoải m&aacute;i, tiện dụng hơn.</p>\r\n\r\n<p><img style=\"width:100%;\" alt=\"\" src=\"https://thietkeroyalhome.com/upload_images/images/2019/09/14/01-khach%20_View01.jpg\" style=\"height:800px; width:1600px\" /></p>\r\n\r\n<p>Chiếc b&agrave;n gỗ to đặt ở giữa tr&ocirc;ng kh&aacute; giống kiểu d&aacute;ng một chiếu ngựa, vừa trang trọng, vừa giữ được c&aacute;i hay trong th&uacute; chơi đồ gỗ của người phương Đ&ocirc;ng v&agrave; đặc biệt l&agrave; &quot;rất Việt Nam&quot;. Bức ho&agrave;nh thủy mặc, gian thờ k&iacute;nh cẩn đặt một b&ecirc;n lại v&ocirc; c&ugrave;ng c&acirc;n bằng với những chiếc đ&egrave;m ch&ugrave;m, những chiếc r&egrave;m cửa.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Những chi tiết &Aacute; Đ&ocirc;ng v&agrave; hiện đại cứ li&ecirc;n tiếp xen kẽ nhau l&agrave;m cho kh&ocirc;ng gian ph&ograve;ng kh&aacute;ch kh&ocirc;ng chỉ đẹp về mặt thẩm mỹ, lại v&ocirc; c&ugrave;ng tiện lợi về c&ocirc;ng năng.</p>\r\n\r\n<p><img style=\"width:100%;\" alt=\"\" src=\"https://thietkeroyalhome.com/upload_images/images/2019/09/14/02-khach%20_View02.jpg\" style=\"height:800px; width:1600px\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Kh&ocirc;ng gian ph&ograve;ng ngủ cũng vậy. Chiếc giường kiểu hiện đại đặt trong một kh&ocirc;ng gian ngập tr&agrave;n c&aacute;c chi tiết phương đ&ocirc;ng nhưng kh&ocirc;ng hề lạc lo&agrave;i. Thực ra n&oacute; lại l&agrave; một t&aacute;c phẩm nữa của sự kết hợp giữa &Aacute; Đ&ocirc;ng v&agrave; hiện đại.</p>\r\n\r\n<p><img style=\"width:100%;\" alt=\"\" src=\"https://thietkeroyalhome.com/upload_images/images/2019/09/14/05-phong%20ngu%20tang%203_View01.jpg\" style=\"height:800px; width:1600px\" /></p>\r\n\r\n<p>C&aacute;c chi tiết ph&iacute;a sau ph&ograve;ng ngủ hay bộ b&agrave;n ghế ngồi đọc s&aacute;ch l&agrave;m to&aacute;t l&ecirc;n một kh&ocirc;ng gian giống một thư ph&ograve;ng, vừa trang trọng lại mang một n&eacute;t ho&agrave;i cổ</p>\r\n\r\n<p><img style=\"width:100%;\" alt=\"\" src=\"https://thietkeroyalhome.com/upload_images/images/2019/09/14/07-phong%20ngu%20tang%203_View03.jpg\" style=\"height:800px; width:1600px\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Khu vực bếp nấu ăn sắp xếp rất gọn g&agrave;ng - một điểm nổi bật trong c&aacute;c thiết kế &Aacute; Đ&ocirc;ng. Kh&ocirc;ng gian m&agrave;u n&acirc;u đỏ sẫm của gỗ, vừa tạo cảm gi&aacute;c sạch sẽ khi nấu ăn lại vừa trang trọng sẵn s&agrave;ng đ&oacute;n tiếp bất cứ vị kh&aacute;ch n&agrave;o tới d&ugrave;ng bữa</p>\r\n\r\n<p><img style=\"width:100%;\" alt=\"\" src=\"https://thietkeroyalhome.com/upload_images/images/2019/09/14/09-bep%20va%20an_View01.jpg\" style=\"height:800px; width:1600px\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Nh&igrave;n chung, kh&ocirc;ng gian sống nh&agrave; anh Chinh rất rộng r&atilde;i bởi sự sắp xếp gọn g&agrave;ng của phong c&aacute;ch &Aacute; Đ&ocirc;ng. Kh&ocirc;ng gian &Aacute; Đ&ocirc;ng ấy được ho&agrave;n thiện th&ecirc;m sự &ecirc;m &aacute;i bởi những chi tiết hiện đại. Đ&oacute; kh&ocirc;ng chỉ đơn thuẩn l&agrave; sự kết hợp giữa n&eacute;t &aacute; đ&ocirc;ng v&agrave; hiện đại để tăng thẩm mỹ, tăng c&ocirc;ng năng m&agrave; c&ograve;n mang những gi&aacute; trị tinh thần, một sự h&ograve;a hợp giữa lối sống hiện đại v&agrave; truyền thống trong lối sống con người.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>H&atilde;y tham khảo cho ng&ocirc;i nh&agrave; m&igrave;nh bạn nh&eacute;!</p>\r\n\r\n<p>--------------------</p>\r\n\r\n<p>ROYAL HOME :</p>\r\n\r\n<p>▸ Tư vấn &amp; thiết kế, thi c&ocirc;ng nội thất TRỌN G&Oacute;I</p>\r\n\r\n<p>▸ L&yacute; tưởng cho NH&Agrave; Ở, CĂN HỘ, BIỆT THỰ CAO CẤP</p>\r\n\r\n<p>▸ Quy tr&igrave;nh r&otilde; r&agrave;ng, đảm bảo thi c&ocirc;ng Đ&Uacute;NG TIẾN ĐỘ</p>\r\n\r\n<p>▸ Ch&iacute;nh s&aacute;ch bảo h&agrave;nh c&ocirc;ng tr&igrave;nh L&Ecirc;N ĐẾN 7 NĂM</p>\r\n\r\n<p>▸ Đội ngũ Kiến tr&uacute;c sư tr&ecirc;n 9 NĂM KINH NGHIỆM</p>\r\n\r\n<p>---------------------</p>\r\n\r\n<p>C&Ocirc;NG TY CP ROYALHOME VIỆT NAM</p>\r\n\r\n<p>➡ Văn ph&ograve;ng: Số 20LK6D &ndash; L&agrave;ng Việt Kiều Ch&acirc;u &Acirc;u &ndash; Mỗ Lao &ndash; H&agrave; Đ&ocirc;ng &ndash; H&agrave; Nội</p>\r\n\r\n<p>➡ Website: http://www.thietkeroyalhome.com</p>\r\n\r\n<p>☎ Hotline: 0966655500</p>\r\n\r\n<p>✉ Email: thietkeroyalhome@gmail.com</p>\r\n', 'struc_img/tt12.jpg', '2019-12-10', 'Sống trong một thế giới hiện đại luôn phát triển cùng với sự đa dạng...');
INSERT INTO `tbl_structures` (`Struc_ID`, `Cate_ID`, `Struc_Name`, `Description`, `Image`) VALUES
('002', 3, 'Biệt Thự Long Biên - Hà Nội', 'Biệt thự được thiết kế theo phong cách Hoàng Gia , với màu vàng là chủ đạo.', 'struc_img/tkbt2.jpg'),
('003', 3, 'Biệt thự Vinhomes Mỹ Đình Gia Lâm', 'Biệt thự gồm có 3 tầng, các khu chức năng được bố trí rõ ràng: phòng khách, bếp, sân vườn, phòng giúp việc, gara, ... Trên web không thể hiện hết được. Quý gia chủ quan tâm hơn vui lòng liên hệ RoyalHome Việt Nam', 'struc_img/tkbt3.jpg'),
('004', 3, 'THIẾT KẾ NỘI THẤT BIỆT THỰ LAI CHÂU', 'Thiết kế theo phong cách Châu Âu , lấy màu xám làm chủ đạo, tạo nên không gian thoáng mát.', 'struc_img/tkbt4.jpg'),
('005', 3, 'Biệt thự Vinhomes Hải Phòng', 'Biệt thự thiết kế theo phong cách hiện đại. Được sơn màu trắng kết hợp với những bức ảnh tạo lên sự nổi bật và sang trọng của ngôi nhà.', 'struc_img/tkbt5.jpg'),
('006', 3, 'Biệt Thự Hạ Long Quảng Ninh', 'Được thiết kế theo phong cách hiện đại . Cửa kính tạo nên sự thoáng đãng rộng rãi nhưng không kém phần sang trọng lịch sự.', 'struc_img/tkbt6.jpg'),
('007', 3, 'Biệt thự Vinhomes Green Bay', 'Được thiết kế với phong cách Châu Âu kết hợp với nhiều chi tiết bằng gỗ đã thể hiện được sự tinh tế của gia chủ.', 'struc_img/tkbt7.jpg'),
('008', 3, 'THIẾT KẾ NỘI NGOẠI THẤT', 'Được thiết kế theo Phong cách Hàn Quốc . Sang trọng quý phái. Sự kết hợp hoàn hảo giữa cổ điển và hiện đại.', 'struc_img/tkbt8.jpg'),
('009', 3, 'THIẾT KẾ NỘI THẤT BIỆT THỰ THÁI BÌNH', 'Được thiết kế theo phong cách cổ điển với nội thật bằng gỗ và giả gỗ cao cấp. Làm cho ngồi nhà trở nên đầy điểm nhấn.', 'struc_img/tkbt9.jpg'),
